      if (showExpertModels) {
        setShowExpertModels(false);
        return;
      }
      if (showMcpServers) {
        setShowMcpServers(false);
        setSelectedMcpServerIndex(0);
        return;
      }
      if (showAddMcpServer) {
        setShowAddMcpServer(false);
        return;
      }
      if (showDeleteMcpServer) {
        setShowDeleteMcpServer(false);
        return;
      }
      if (isProcessing || isStreaming) {
        agent.abortCurrentOperation();
        setIsProcessing(false);
        setIsStreaming(false);
        setTokenCount(0);
        setProcessingTime(0);
        processingStartTime.current = 0;
        return;
      }
    }

    if (showCommandSuggestions) {
      const filteredSuggestions = getFilteredSuggestions(input);

      if (key.upArrow) {
        setSelectedCommandIndex((prev) =>
          prev === 0 ? filteredSuggestions.length - 1 : prev - 1
        );
        return;
      }
      if (key.downArrow) {
        setSelectedCommandIndex((prev) => (prev + 1) % filteredSuggestions.length);
        return;
      }
      if (key.tab || key.return) {
        const selectedCommand = filteredSuggestions[selectedCommandIndex];
        if (selectedCommand) {
          setInput(selectedCommand.command + " ");
          setShowCommandSuggestions(false);
          setSelectedCommandIndex(0);
        }
        return;
      }
    }

    if (showModelSelection) {
      if (key.upArrow) {
        setSelectedModelIndex((prev) =>
          prev === 0 ? dynamicModels.length - 1 : prev - 1
        );
        return;
      }
      if (key.downArrow) {
        setSelectedModelIndex((prev) => (prev + 1) % dynamicModels.length);
        return;
      }
      if (key.tab || key.return) {
        const selectedModel = dynamicModels[selectedModelIndex];
        onModelSelected(selectedModel.model);
        agent.setModel(selectedModel.model);
        const confirmEntry: ChatEntry = {
          type: "assistant",
          content: `‚úì Switched to model: ${selectedModel.model}`,
          timestamp: new Date(),
        };
        setChatHistory((prev) => [...prev, confirmEntry]);
        setShowModelSelection(false);
        setSelectedModelIndex(0);
        return;
      }
    }

    if (showMcpServers) {
      if (key.upArrow) {
        setSelectedMcpServerIndex((prev) =>
          prev === 0 ? mcpServers.length - 1 : prev - 1
        );
        return;
      }
      if (key.downArrow) {
        setSelectedMcpServerIndex((prev) => (prev + 1) % mcpServers.length);
        return;
      }
      if (key.tab || key.return) {
        const selectedServer = mcpServers[selectedMcpServerIndex];
        if (selectedServer) {
          const serverInfo: ChatEntry = {
            type: "assistant",
            content: `MCP Server: ${selectedServer.name}
Command: ${selectedServer.command}
${selectedServer.args ? `Args: ${selectedServer.args.join(' ')}` : ''}
${selectedServer.env ? `Environment: ${Object.entries(selectedServer.env).map(([k, v]) => `${k}=${v}`).join(' ')}` : ''}
${selectedServer.description ? `Description: ${selectedServer.description}` : ''}
Added: ${new Date(selectedServer.dateAdded).toLocaleDateString()}`,
            timestamp: new Date(),
          };
          setChatHistory((prev) => [...prev, serverInfo]);
        }
        setShowMcpServers(false);
        setSelectedMcpServerIndex(0);
        return;
      }
    }

    if (showAddModel || showDeleteModel || showAddMcpServer || showDeleteMcpServer) {
      return;
    }

    if (showProviderSettings) {
      return;
    }

    // Handle file finder navigation when active (takes precedence over history)
    if (showFileFinder && filteredFiles.length > 0) {
      if (key.upArrow) {
        setSelectedFileIndex((prev) =>
          prev === 0 ? filteredFiles.length - 1 : prev - 1
        );
        return;
      }
      if (key.downArrow) {
        setSelectedFileIndex((prev) => (prev + 1) % filteredFiles.length);
        return;
      }
      if (key.return || key.tab) {
        const selectedFile = filteredFiles[selectedFileIndex];
        if (selectedFile) {
          const newInput = replaceFileQuery(input, selectedFile);
          setInput(newInput);
          closeFileFinder();
        }
        return;
      }
    }

    // Handle command history navigation with up/down arrows
    if (key.upArrow && !showCommandSuggestions && !showModelSelection && !showMcpServers) {
      if (commandHistory.length > 0) {
        if (historyIndex === -1) {
          // Store current input before navigating history
          setTemporaryInput(input);
          setHistoryIndex(commandHistory.length - 1);
          setInput(commandHistory[commandHistory.length - 1]);
        } else if (historyIndex > 0) {
          setHistoryIndex(historyIndex - 1);
          setInput(commandHistory[historyIndex - 1]);
        }
      }
      return;
    }

    if (key.downArrow && !showCommandSuggestions && !showModelSelection && !showMcpServers) {
      if (commandHistory.length > 0 && historyIndex !== -1) {
        if (historyIndex < commandHistory.length - 1) {
          setHistoryIndex(historyIndex + 1);
          setInput(commandHistory[historyIndex + 1]);
        } else {
          // Return to original input
          setHistoryIndex(-1);
          setInput(temporaryInput);
        }
      }
      return;
    }

    if (key.return) {
      const userInput = input.trim();
      if (userInput === "exit" || userInput === "quit") {
        exit();
        return;
      }

      if (userInput) {
        const directCommandResult = await handleDirectCommand(userInput);
        if (!directCommandResult) {
          await processUserMessage(userInput);
        }
      }
      return;
    }

    if (key.backspace || key.delete) {
      const newInput = input.slice(0, -1);
      setInput(newInput);

      // Reset history navigation when user edits input
      if (historyIndex !== -1) {
        setHistoryIndex(-1);
        setTemporaryInput("");
      }

      // Update file finder
      updateFileFinder(newInput);

      if (!newInput.startsWith("/")) {
        setShowCommandSuggestions(false);
        setSelectedCommandIndex(0);
      } else if (showCommandSuggestions) {
        // Reset selected index when input changes to avoid out-of-bounds
        setSelectedCommandIndex(0);
      }
      return;
    }

    if (inputChar && !key.ctrl && !key.meta) {
      const newInput = input + inputChar;
      setInput(newInput);

      // Reset history navigation when user types new input
      if (historyIndex !== -1) {
        setHistoryIndex(-1);
        setTemporaryInput("");
      }

      // Update file finder based on @ symbol
      updateFileFinder(newInput);

      if (
        newInput === "/" ||
        ["ls", "pwd", "cd", "cat", "mkdir", "touch"].some((cmd) =>
          cmd.startsWith(newInput)
        )
      ) {
        setShowCommandSuggestions(true);
        setSelectedCommandIndex(0);
      } else if (
        !newInput.startsWith("/") &&
        !["ls", "pwd", "cd", "cat", "mkdir", "touch"].some((cmd) =>
          cmd.startsWith(newInput)
        )
      ) {
        setShowCommandSuggestions(false);
        setSelectedCommandIndex(0);
      } else if (showCommandSuggestions) {
        // Reset selected index when input changes to avoid out-of-bounds
        setSelectedCommandIndex(0);
      }
    }
  });

  return {
    input,
    showCommandSuggestions,
    selectedCommandIndex,
    showModelSelection,
    selectedModelIndex,
    showProviderSettings,
    selectedProviderIndex,
    showAddModel,
    showDeleteModel,
    showPromptsList,
    showAddPrompt,
    showDeletePrompt,
    showMcpServers,
    showAddMcpServer,
    showDeleteMcpServer,
    selectedMcpServerIndex,
    showConversationHistory,
    showTemperatureSelector,
    currentTemperature,
    showExpertModels,
    showRouteSelection,
    routeViewMode,
    selectedRouteModelIndex,
    selectedRouteProviderIndex,
    currentSelectedModel,
    routeProviders,
    isLoadingProviders,
    showFileFinder,
    selectedFileIndex,
    filteredFiles,
    fileQuery,
    commandSuggestions,
    availableModels: dynamicModels,
    mcpServers,
    providerList,
    closeProviderSettings,
    closeAddModel,
    closeDeleteModel,
    closePromptsList,
    closeAddPrompt,
    closeDeletePrompt,
    closeMcpServers,
    closeAddMcpServer,
    closeDeleteMcpServer,
    closeConversationHistory,
    closeTemperatureSelector,
    closeExpertModels,
    closeRouteSelection,
    closeFileFinder,
    refreshModels,
    refreshMcpServers,
    openRouterModels: getOpenRouterModels(dynamicModels),
    agent,
  };
}

#!/usr/bin/env node

import React from "react";
import { render } from "ink";
import { program } from "commander";
import * as dotenv from "dotenv";
import { GigaAgent } from "./agent/giga-agent";
import ChatInterface from "./ui/components/chat-interface";
import { ConfirmationService } from "./utils/confirmation-service";
import * as fs from "fs";
import * as path from "path";
import * as os from "os";

// Load environment variables
dotenv.config();

import { loadApiKeys } from "./utils/api-keys";

// We'll set up the double Ctrl+C handler after Ink starts

// Check if any API keys are available from environment variables, shell files, or settings
function hasAnyApiKey(): boolean {
  const apiKeys = loadApiKeys();
  const availableKeys = Object.values(apiKeys).filter(Boolean);
  return availableKeys.length > 0;
}

program
  .name("giga")
  .description(
    "A conversational AI CLI tool powered by Grok-3 with text editor capabilities"
  )
  .version("1.0.0")
  .option("-p, --prompt <prompt>", "run a single prompt in headless mode")
  .option("-d, --directory <dir>", "set working directory", process.cwd())
  .action(async (options) => {
    if (options.directory) {
      try {
        process.chdir(options.directory);
      } catch (error: any) {
        console.error(
          `‚ùå Error changing directory to ${options.directory}:`,
          error.message
        );
        process.exit(1);
      }
    }

    // If prompt is provided, run in headless mode
    if (options.prompt) {
      try {
        // Enable headless mode in confirmation service
        const confirmationService = ConfirmationService.getInstance();
        confirmationService.setHeadlessMode(true);

        // Check if any API keys are available
        const hasKeys = hasAnyApiKey();

        if (!hasKeys) {
          console.error("‚ùå Error: API key is required. Set environment variables or configure in settings");
          process.exit(1);
        }

        const agent = new GigaAgent('', '');

        // Check if a model is configured
        const currentModel = agent.getCurrentModel();
        if (!currentModel) {
          console.error("‚ùå No model configured. Please set up giga first:");
          console.error("1. Run 'giga' to enter interactive mode");
          console.error("2. Configure API keys: /providers");
          console.error("3. Add models: /add-model");
          console.error("4. Select a model: /models");
          process.exit(1);
        }

        console.log(`ü§ñ Processing prompt: ${options.prompt}`);
        console.log(`üìÅ Working directory: ${process.cwd()}\n`);

        // Process the prompt and stream results
        for await (const chunk of agent.processUserMessageStream(options.prompt)) {
          if (chunk.type === 'content') {
            process.stdout.write(chunk.content || '');
          } else if (chunk.type === 'tool_calls') {
            console.log(`\nüîß Using tools: ${chunk.toolCalls?.map(tc => tc.function.name).join(', ')}`);
          } else if (chunk.type === 'tool_result') {
            if (!chunk.toolResult?.success) {
              console.log(`\n‚ùå Tool error: ${chunk.toolResult?.error}`);
            }
          } else if (chunk.type === 'done') {
            console.log('\n\n‚úÖ Done');
            break;
          }
        }
      } catch (error: any) {
        console.error("‚ùå Error executing prompt:", error.message);
        process.exit(1);
      }
    } else {
      // Run in interactive mode
      try {
        // Check if any API keys are available
        const hasKeys = hasAnyApiKey();
        const agent = hasKeys ? new GigaAgent('', '') : undefined;

        console.log("ü§ñ Starting GIGA Conversational Assistant...\n");

        const app = render(React.createElement(ChatInterface, { agent }));

        // Set up double Ctrl+C handler after Ink is running
        let lastCtrlCTime = 0;
        let ctrlCTimeout: NodeJS.Timeout | null = null;

        // Remove any existing SIGINT listeners first
        process.removeAllListeners('SIGINT');

        process.on('SIGINT', () => {
          const now = Date.now();
          const timeSinceLastCtrlC = now - lastCtrlCTime;

          if (timeSinceLastCtrlC < 1000 && lastCtrlCTime > 0) {
            // Second Ctrl+C within 1 second - exit immediately
            if (ctrlCTimeout) {
              clearTimeout(ctrlCTimeout);
              ctrlCTimeout = null;
            }
            console.log('\nüëã Goodbye!');
            app.unmount();
            process.exit(0);
          } else {
            // First Ctrl+C or too late - show message and start timer
            lastCtrlCTime = now;
            console.log('\nPress Ctrl+C again within 1 second to exit');

            // Clear any existing timeout
            if (ctrlCTimeout) {
              clearTimeout(ctrlCTimeout);
            }

            // Reset the timer after 1 second
            ctrlCTimeout = setTimeout(() => {
              lastCtrlCTime = 0;
              ctrlCTimeout = null;
            }, 1000);
          }
        });
      } catch (error: any) {
        console.error("‚ùå Error initializing GIGA:", error.message);
        process.exit(1);
      }
    }
  });

program.parse();

import { spawn, ChildProcess } from 'child_process';
import { AddedMcpServer } from '../utils/added-mcp-servers';

export interface McpTool {
  name: string;
  description?: string;
  inputSchema: any;
}

export interface McpResource {
  uri: string;
  name: string;
  description?: string;
  mimeType?: string;
}

export interface McpServerInfo {
  name: string;
  version: string;
  tools?: McpTool[];
  resources?: McpResource[];
}

export interface McpToolCall {
  name: string;
  arguments: any;
}

export interface McpToolResult {
  content?: Array<{
    type: string;
    text?: string;
    data?: string;
    mimeType?: string;
  }>;
  isError?: boolean;
  _meta?: any;
}

export class McpClient {
  private process: ChildProcess | null = null;
  private server: AddedMcpServer;
  private messageId = 0;
  private pendingRequests = new Map<number, { resolve: Function; reject: Function }>();
  private isConnected = false;
  private serverInfo: McpServerInfo | null = null;

  constructor(server: AddedMcpServer) {
    this.server = server;
  }

  async connect(): Promise<void> {
    return new Promise((resolve, reject) => {
      try {
        // Parse command and args
        const commandParts = this.server.command.split(' ');
        const command = commandParts[0];
        const args = [...commandParts.slice(1), ...(this.server.args || [])];

        // Spawn the MCP server process
        this.process = spawn(command, args, {
          stdio: ['pipe', 'pipe', 'pipe'],
          env: { ...process.env, ...this.server.env },
        });

        this.process.on('error', (error) => {
          reject(new Error(`Failed to start MCP server: ${error.message}`));
        });

        // Set up message handling
        let buffer = '';
        this.process.stdout?.on('data', (data) => {
          buffer += data.toString();

          // Process complete JSON-RPC messages
          let newlineIndex;
          while ((newlineIndex = buffer.indexOf('\n')) !== -1) {
            const line = buffer.slice(0, newlineIndex).trim();
            buffer = buffer.slice(newlineIndex + 1);

            if (line) {
              try {
                const message = JSON.parse(line);
                this.handleMessage(message);
              } catch (error) {
                console.error('Failed to parse MCP message:', error, 'Line:', line);
              }
            }
          }
        });

        this.process.stderr?.on('data', (data) => {
          console.error(`MCP server stderr: ${data}`);
        });

        this.process.on('close', (code) => {
          this.isConnected = false;
          if (code !== 0) {
            console.error(`MCP server exited with code ${code}`);
          }
        });

        // Initialize the connection
        this.initialize().then(() => {
          this.isConnected = true;
          resolve();
        }).catch(reject);

      } catch (error) {
        reject(error);
      }
    });
  }

  private async initialize(): Promise<void> {
    // Send initialize request
    const initResponse = await this.sendRequest('initialize', {
      protocolVersion: '2024-11-05',
      capabilities: {
        tools: {},
      },
      clientInfo: {
        name: 'giga-code',
        version: '1.0.0',
      },
    });

    this.serverInfo = {
      name: initResponse.serverInfo?.name || this.server.name,
      version: initResponse.serverInfo?.version || '1.0.0',
    };

    // Send initialized notification
    this.sendNotification('initialized', {});

    // Get available tools
    try {
      const toolsResponse = await this.sendRequest('tools/list', {});
      if (toolsResponse.tools) {
        this.serverInfo.tools = toolsResponse.tools;
      }
    } catch (error) {
      console.warn('Failed to get tools list:', error);
      this.serverInfo.tools = [];
    }

    // Get available resources
    try {
      const resourcesResponse = await this.sendRequest('resources/list', {});
      if (resourcesResponse.resources) {
        this.serverInfo.resources = resourcesResponse.resources;
      }
    } catch (error) {
      console.warn('Failed to get resources list:', error);
      this.serverInfo.resources = [];
    }
  }

  private handleMessage(message: any): void {
    if (message.id !== undefined && this.pendingRequests.has(message.id)) {
      // This is a response to a request
      const { resolve, reject } = this.pendingRequests.get(message.id)!;
      this.pendingRequests.delete(message.id);

      if (message.error) {
        reject(new Error(message.error.message || 'MCP error'));
      } else {
        resolve(message.result);
      }
    } else {
      // This is a notification or request from the server
      console.log('Received MCP message:', message);
    }
  }

  private sendRequest(method: string, params: any): Promise<any> {
    return new Promise((resolve, reject) => {
      if (!this.process || !this.isConnected) {
        reject(new Error('MCP client not connected'));
        return;
      }

      const id = ++this.messageId;
      const message = {
        jsonrpc: '2.0',
        id,
        method,
        params,
      };

      this.pendingRequests.set(id, { resolve, reject });

      const messageStr = JSON.stringify(message) + '\n';
      this.process.stdin?.write(messageStr);

      // Set timeout for requests
      setTimeout(() => {
        if (this.pendingRequests.has(id)) {
          this.pendingRequests.delete(id);
          reject(new Error('MCP request timeout'));
        }
      }, 30000); // 30 second timeout
    });
  }

  private sendNotification(method: string, params: any): void {
    if (!this.process || !this.isConnected) {
      return;
    }

    const message = {
      jsonrpc: '2.0',
      method,
      params,
    };

    const messageStr = JSON.stringify(message) + '\n';
    this.process.stdin?.write(messageStr);
  }

  async callTool(name: string, arguments_: any): Promise<McpToolResult> {
    try {
      const response = await this.sendRequest('tools/call', {
        name,
        arguments: arguments_,
      });

      return {
        content: response.content || [],
        isError: response.isError || false,
        _meta: response._meta,
      };
    } catch (error) {
      return {
        content: [{
          type: 'text',
          text: `Error calling tool: ${error instanceof Error ? error.message : String(error)}`,
        }],
        isError: true,
      };
    }
  }

  getServerInfo(): McpServerInfo | null {
    return this.serverInfo;
  }

  getTools(): McpTool[] {
    return this.serverInfo?.tools || [];
  }

  getResources(): McpResource[] {
    return this.serverInfo?.resources || [];
  }

  async disconnect(): Promise<void> {
    if (this.process) {
      this.process.kill();
      this.process = null;
    }
    this.isConnected = false;
    this.pendingRequests.clear();
  }

  isConnectedToServer(): boolean {
    return this.isConnected;
  }
}

import { McpClient, McpTool, McpToolResult } from './mcp-client';
import { loadAddedMcpServers, AddedMcpServer } from '../utils/added-mcp-servers';

export interface McpToolWithServer extends McpTool {
  serverName: string;
}

export class McpManager {
  private clients = new Map<string, McpClient>();
  private static instance: McpManager | null = null;

  static getInstance(): McpManager {
    if (!McpManager.instance) {
      McpManager.instance = new McpManager();
    }
    return McpManager.instance;
  }

  async initializeAllServers(): Promise<void> {
    const servers = loadAddedMcpServers();
    const connectionPromises = servers.map(server => this.connectToServer(server));

    // Connect to all servers, but don't fail if some connections fail
    const results = await Promise.allSettled(connectionPromises);

    results.forEach((result, index) => {
      if (result.status === 'rejected') {
        console.error(`Failed to connect to MCP server ${servers[index].name}:`, result.reason);
      }
    });
  }

  async connectToServer(server: AddedMcpServer): Promise<McpClient> {
    const existingClient = this.clients.get(server.name);
    if (existingClient && existingClient.isConnectedToServer()) {
      return existingClient;
    }

    const client = new McpClient(server);
    try {
      await client.connect();
      this.clients.set(server.name, client);
      console.log(`Connected to MCP server: ${server.name}`);
      return client;
    } catch (error) {
      console.error(`Failed to connect to MCP server ${server.name}:`, error);
      throw error;
    }
  }

  async disconnectFromServer(serverName: string): Promise<void> {
    const client = this.clients.get(serverName);
    if (client) {
      await client.disconnect();
      this.clients.delete(serverName);
      console.log(`Disconnected from MCP server: ${serverName}`);
    }
  }

  async disconnectAll(): Promise<void> {
    const disconnectPromises = Array.from(this.clients.keys()).map(serverName => 
      this.disconnectFromServer(serverName)
    );
    await Promise.all(disconnectPromises);
  }

  getAllTools(): McpToolWithServer[] {
    const tools: McpToolWithServer[] = [];

    for (const [serverName, client] of this.clients) {
      if (client.isConnectedToServer()) {
        const serverTools = client.getTools();
        serverTools.forEach(tool => {
          tools.push({
            ...tool,
            serverName,
          });
        });
      }
    }

    return tools;
  }

  getToolsByServer(serverName: string): McpTool[] {
    const client = this.clients.get(serverName);
    if (client && client.isConnectedToServer()) {
      return client.getTools();
    }
    return [];
  }

  async callTool(serverName: string, toolName: string, arguments_: any): Promise<McpToolResult> {
    const client = this.clients.get(serverName);
    if (!client) {
      return {
        content: [{
          type: 'text',
          text: `MCP server '${serverName}' not found or not connected`,
        }],
        isError: true,
      };
    }

    if (!client.isConnectedToServer()) {
      return {
        content: [{
          type: 'text',
          text: `MCP server '${serverName}' is not connected`,
        }],
        isError: true,
      };
    }

    return await client.callTool(toolName, arguments_);
  }

  findToolByName(toolName: string): McpToolWithServer | null {
    const allTools = this.getAllTools();
    return allTools.find(tool => tool.name === toolName) || null;
  }

  getConnectedServers(): string[] {
    const connectedServers: string[] = [];
    for (const [serverName, client] of this.clients) {
      if (client.isConnectedToServer()) {
        connectedServers.push(serverName);
      }
    }
    return connectedServers;
  }

  getServerInfo(serverName: string) {
    const client = this.clients.get(serverName);
    if (client && client.isConnectedToServer()) {
      return client.getServerInfo();
    }
    return null;
  }

  isServerConnected(serverName: string): boolean {
    const client = this.clients.get(serverName);
    return client ? client.isConnectedToServer() : false;
  }

  async refreshConnections(): Promise<void> {
    // Reload servers from storage and connect to any new ones
    const servers = loadAddedMcpServers();
    const currentServers = new Set(this.clients.keys());
    const newServers = servers.filter(server => !currentServers.has(server.name));

    // Connect to new servers
    const connectionPromises = newServers.map(server => this.connectToServer(server));
    const results = await Promise.allSettled(connectionPromises);

    results.forEach((result, index) => {
      if (result.status === 'rejected') {
        console.error(`Failed to connect to new MCP server ${newServers[index].name}:`, result.reason);
      }
    });

    // Disconnect from servers that are no longer in the configuration
    const configuredServerNames = new Set(servers.map(s => s.name));
    for (const serverName of currentServers) {
      if (!configuredServerNames.has(serverName)) {
        await this.disconnectFromServer(serverName);
      }
    }
  }
}

import { exec } from 'child_process';
import { promisify } from 'util';
import { ToolResult } from '../types';
import { ConfirmationService } from '../utils/confirmation-service';

const execAsync = promisify(exec);

export class BashTool {
  private currentDirectory: string = process.cwd();
  private confirmationService = ConfirmationService.getInstance();


  async execute(command: string, timeout: number = 30000): Promise<ToolResult> {
    try {
      // Check if user has already accepted bash commands for this session
      const sessionFlags = this.confirmationService.getSessionFlags();
      if (!sessionFlags.bashCommands && !sessionFlags.allOperations) {
        // Request confirmation showing the command
        const confirmationResult = await this.confirmationService.requestConfirmation({
          operation: 'Run bash command',
          filename: command,
          showVSCodeOpen: false,
          content: `Command: ${command}\nWorking directory: ${this.currentDirectory}`
        }, 'bash');

        if (!confirmationResult.confirmed) {
          return {
            success: false,
            error: confirmationResult.feedback || 'Command execution cancelled by user'
          };
        }
      }

      if (command.startsWith('cd ')) {
        const newDir = command.substring(3).trim();
        try {
          process.chdir(newDir);
          this.currentDirectory = process.cwd();
          return {
            success: true,
            output: `Changed directory to: ${this.currentDirectory}`
          };
        } catch (error: any) {
          return {
            success: false,
            error: `Cannot change directory: ${error.message}`
          };
        }
      }

      const { stdout, stderr } = await execAsync(command, {
        cwd: this.currentDirectory,
        timeout,
        maxBuffer: 1024 * 1024
      });

      const output = stdout + (stderr ? `\nSTDERR: ${stderr}` : '');

      return {
        success: true,
        output: output.trim() || 'Command executed successfully (no output)'
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Command failed: ${error.message}`
      };
    }
  }

  getCurrentDirectory(): string {
    return this.currentDirectory;
  }

  async listFiles(directory: string = '.'): Promise<ToolResult> {
    return this.execute(`ls -la ${directory}`);
  }

  async findFiles(pattern: string, directory: string = '.'): Promise<ToolResult> {
    return this.execute(`find ${directory} -name "${pattern}" -type f`);
  }

  async grep(pattern: string, files: string = '.'): Promise<ToolResult> {
    return this.execute(`grep -r "${pattern}" ${files}`);
  }
}

import { ToolResult } from '../types';
import { ConfirmationService, ConfirmationOptions } from '../utils/confirmation-service';

export interface ConfirmationRequest {
  operation: string;
  filename: string;
  description?: string;
  showVSCodeOpen?: boolean;
  autoAccept?: boolean;
}

export class ConfirmationTool {
  private confirmationService: ConfirmationService;

  constructor() {
    this.confirmationService = ConfirmationService.getInstance();
  }

  async requestConfirmation(request: ConfirmationRequest): Promise<ToolResult> {
    try {
      // If autoAccept is true, skip the confirmation dialog
      if (request.autoAccept) {
        return {
          success: true,
          output: `Auto-accepted: ${request.operation}(${request.filename})${request.description ? ` - ${request.description}` : ''}`
        };
      }

      const options: ConfirmationOptions = {
        operation: request.operation,
        filename: request.filename,
        showVSCodeOpen: request.showVSCodeOpen || false
      };

      // Determine operation type based on operation name
      const operationType = request.operation.toLowerCase().includes('bash') ? 'bash' : 'file';
      const result = await this.confirmationService.requestConfirmation(options, operationType);

      if (result.confirmed) {
        return {
          success: true,
          output: `User confirmed: ${request.operation}(${request.filename})${request.description ? ` - ${request.description}` : ''}${result.dontAskAgain ? ' (Don\'t ask again enabled)' : ''}`
        };
      } else {
        return {
          success: false,
          error: result.feedback || `User rejected: ${request.operation}(${request.filename})`
        };
      }
    } catch (error: any) {
      return {
        success: false,
        error: `Confirmation error: ${error.message}`
      };
    }
  }

  async checkSessionAcceptance(): Promise<ToolResult> {
    try {
      const sessionFlags = this.confirmationService.getSessionFlags();
      // Return structured data without JSON output to avoid displaying raw JSON
      return {
        success: true,
        data: {
          fileOperationsAccepted: sessionFlags.fileOperations,
          bashCommandsAccepted: sessionFlags.bashCommands,
          allOperationsAccepted: sessionFlags.allOperations,
          hasAnyAcceptance: sessionFlags.fileOperations || sessionFlags.bashCommands || sessionFlags.allOperations
        }
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Error checking session acceptance: ${error.message}`
      };
    }
  }

  resetSession(): void {
    this.confirmationService.resetSession();
  }

  isPending(): boolean {
    return this.confirmationService.isPending();
  }
}

