export class McpTool {
  private mcpManager: McpManager;

  constructor() {
    this.mcpManager = McpManager.getInstance();
  }

  async listMcpServers(): Promise<ToolResult> {
    try {
      const connectedServers = this.mcpManager.getConnectedServers();

      if (connectedServers.length === 0) {
        return {
          success: true,
          output: 'No MCP servers are currently connected.',
        };
      }

      let output = 'Connected MCP Servers:\n\n';

      for (const serverName of connectedServers) {
        const serverInfo = this.mcpManager.getServerInfo(serverName);
        const tools = this.mcpManager.getToolsByServer(serverName);

        output += `üì° ${serverName}\n`;
        if (serverInfo) {
          output += `   Version: ${serverInfo.version}\n`;
        }
        output += `   Tools: ${tools.length}\n`;

        if (tools.length > 0) {
          output += `   Available tools:\n`;
          tools.forEach(tool => {
            output += `     ‚Ä¢ ${tool.name}`;
            if (tool.description) {
              output += ` - ${tool.description}`;
            }
            output += '\n';
          });
        }
        output += '\n';
      }

      return {
        success: true,
        output,
      };
    } catch (error) {
      return {
        success: false,
        error: `Failed to list MCP servers: ${error instanceof Error ? error.message : String(error)}`,
      };
    }
  }

  async listMcpTools(): Promise<ToolResult> {
    try {
      const allTools = this.mcpManager.getAllTools();

      if (allTools.length === 0) {
        return {
          success: true,
          output: 'No MCP tools are currently available.',
        };
      }

      let output = 'Available MCP Tools:\n\n';

      const toolsByServer = new Map<string, typeof allTools>();
      allTools.forEach(tool => {
        if (!toolsByServer.has(tool.serverName)) {
          toolsByServer.set(tool.serverName, []);
        }
        toolsByServer.get(tool.serverName)!.push(tool);
      });

      for (const [serverName, tools] of toolsByServer) {
        output += `üì° ${serverName}:\n`;
        tools.forEach(tool => {
          output += `   üîß ${tool.name}`;
          if (tool.description) {
            output += ` - ${tool.description}`;
          }
          output += '\n';
        });
        output += '\n';
      }

      return {
        success: true,
        output,
      };
    } catch (error) {
      return {
        success: false,
        error: `Failed to list MCP tools: ${error instanceof Error ? error.message : String(error)}`,
      };
    }
  }

  async callMcpTool(toolName: string, arguments_: any): Promise<ToolResult> {
    try {
      const tool = this.mcpManager.findToolByName(toolName);

      if (!tool) {
        const availableTools = this.mcpManager.getAllTools();
        const toolNames = availableTools.map(t => t.name).join(', ');
        return {
          success: false,
          error: `MCP tool '${toolName}' not found. Available tools: ${toolNames}`,
        };
      }

      const result = await this.mcpManager.callTool(tool.serverName, toolName, arguments_);

      if (result.isError) {
        const errorMessage = result.content?.find(c => c.type === 'text')?.text || 'Unknown error';
        return {
          success: false,
          error: `MCP tool error: ${errorMessage}`,
        };
      }

      // Convert MCP result to tool result
      let output = '';
      if (result.content) {
        for (const content of result.content) {
          if (content.type === 'text' && content.text) {
            output += content.text + '\n';
          } else if (content.type === 'resource' && content.data) {
            output += `[Resource: ${content.mimeType || 'unknown'}]\n${content.data}\n`;
          }
        }
      }

      return {
        success: true,
        output: output.trim() || 'Tool executed successfully (no output)',
      };
    } catch (error) {
      return {
        success: false,
        error: `Failed to call MCP tool: ${error instanceof Error ? error.message : String(error)}`,
      };
    }
  }

  async refreshMcpConnections(): Promise<ToolResult> {
    try {
      await this.mcpManager.refreshConnections();
      return {
        success: true,
        output: 'MCP server connections refreshed successfully.',
      };
    } catch (error) {
      return {
        success: false,
        error: `Failed to refresh MCP connections: ${error instanceof Error ? error.message : String(error)}`,
      };
    }
  }
}

import { ToolResult } from '../types';
import { loadApiKeys } from '../utils/api-keys';

export class PerplexityTool {
  private openRouterApiKey: string | null;

  constructor() {
    const apiKeys = loadApiKeys();
    this.openRouterApiKey = apiKeys.openRouterApiKey || process.env.OPENROUTER_API_KEY || null;
  }

  async search(query: string, maxResults: number = 10, summarize: boolean = true): Promise<ToolResult> {
    if (!this.openRouterApiKey) {
      return {
        success: false,
        error: 'OpenRouter API key is required for Perplexity search. Please configure it in /providers.'
      };
    }

    try {
      const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.openRouterApiKey}`,
          'Content-Type': 'application/json',
          'HTTP-Referer': 'https://giga-code.dev',
          'X-Title': 'GIGA Code'
        },
        body: JSON.stringify({
          model: 'perplexity/sonar-pro',
          messages: [
            {
              role: 'system',
              content: `You are a helpful research assistant. Provide comprehensive, accurate, and up-to-date information based on web search results. Include relevant links and sources when possible. Structure your response clearly with headings and bullet points where appropriate.`
            },
            {
              role: 'user',
              content: query
            }
          ],
          max_tokens: 2000,
          temperature: 0.1,
          stream: false
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        return {
          success: false,
          error: `Perplexity search failed: ${response.status} ${response.statusText}\n${errorText}`
        };
      }

      const data = await response.json() as any;
      const searchResult = data.choices?.[0]?.message?.content;

      if (!searchResult) {
        return {
          success: false,
          error: 'No search results returned from Perplexity'
        };
      }

      // Create summary for user display
      let userSummary = '';
      try {
        const summaryResponse = await fetch('https://api.groq.com/openai/v1/chat/completions', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${loadApiKeys().groqApiKey || process.env.GROQ_API_KEY}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            model: 'llama-3.3-70b-versatile',
            messages: [
              {
                role: 'system',
                content: 'Create a single concise sentence (max 100 chars) that captures the key finding from the search results. Be direct and actionable.'
              },
              {
                role: 'user',
                content: `Query: "${query}"\n\nSearch Results:\n${searchResult}\n\nOne-line summary:`
              }
            ],
            max_tokens: 50,
            temperature: 0.1
          })
        });

        if (summaryResponse.ok) {
          const summaryData = await summaryResponse.json() as any;
          userSummary = summaryData.choices?.[0]?.message?.content?.trim() || '';
        }
      } catch (error) {
        userSummary = 'Search completed successfully';
      }

      // Return structured result with both summary and full content
      return {
        success: true,
        output: `üîç **Search Results for: "${query}"**\n\n${searchResult}`, // Full results for model context
        data: {
          userSummary: userSummary || `Found information about: ${query}`,
          query: query
        }
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Perplexity search error: ${error.message}`
      };
    }
  }
}

import * as fs from "fs-extra";
import * as path from "path";
import { ToolResult, EditorCommand } from "../types";
import { ConfirmationService } from "../utils/confirmation-service";

export class TextEditorTool {
  private editHistory: EditorCommand[] = [];
  private confirmationService = ConfirmationService.getInstance();

  async view(
    filePath: string,
    viewRange?: [number, number]
  ): Promise<ToolResult> {
    try {
      const resolvedPath = path.resolve(filePath);

      if (await fs.pathExists(resolvedPath)) {
        const stats = await fs.stat(resolvedPath);

        if (stats.isDirectory()) {
          const files = await fs.readdir(resolvedPath);
          return {
            success: true,
            output: `Directory contents of ${filePath}:\n${files.join("\n")}`,
          };
        }

        const content = await fs.readFile(resolvedPath, "utf-8");
        const lines = content.split("\n");

        if (viewRange) {
          const [start, end] = viewRange;
          const selectedLines = lines.slice(start - 1, end);
          const numberedLines = selectedLines
            .map((line, idx) => `${start + idx}: ${line}`)
            .join("\n");

          return {
            success: true,
            output: `Lines ${start}-${end} of ${filePath}:\n${numberedLines}`,
          };
        }

        const totalLines = lines.length;
        const displayLines = totalLines > 10 ? lines.slice(0, 10) : lines;
        const numberedLines = displayLines
          .map((line, idx) => `${idx + 1}: ${line}`)
          .join("\n");
        const additionalLinesMessage =
          totalLines > 10 ? `\n... +${totalLines - 10} lines` : "";

        return {
          success: true,
          output: `Contents of ${filePath}:\n${numberedLines}${additionalLinesMessage}`,
        };
      } else {
        return {
          success: false,
          error: `File or directory not found: ${filePath}`,
        };
      }
    } catch (error: any) {
      return {
        success: false,
        error: `Error viewing ${filePath}: ${error.message}`,
      };
    }
  }

  async strReplace(
    filePath: string,
    oldStr: string,
    newStr: string
  ): Promise<ToolResult> {
    try {
      const resolvedPath = path.resolve(filePath);

      if (!(await fs.pathExists(resolvedPath))) {
        return {
          success: false,
          error: `File not found: ${filePath}`,
        };
      }

      const content = await fs.readFile(resolvedPath, "utf-8");

      if (!content.includes(oldStr)) {
        return {
          success: false,
          error: `String not found in file: "${oldStr}"`,
        };
      }

      // Check if user has already accepted file operations for this session
      const sessionFlags = this.confirmationService.getSessionFlags();
      if (!sessionFlags.fileOperations && !sessionFlags.allOperations) {
        // Create a proper diff preview showing the change
        const newContent = content.replace(oldStr, newStr);
        const oldLines = content.split("\n");
        const newLines = newContent.split("\n");
        const diffContent = this.generateDiff(oldLines, newLines, filePath);

        const confirmationResult =
          await this.confirmationService.requestConfirmation(
            {
              operation: "Edit file",
              filename: filePath,
              showVSCodeOpen: false,
              content: diffContent,
            },
            "file"
          );

        if (!confirmationResult.confirmed) {
          return {
            success: false,
            error: confirmationResult.feedback || "File edit cancelled by user",
          };
        }
      }

      const newContent = content.replace(oldStr, newStr);
      await fs.writeFile(resolvedPath, newContent, "utf-8");

      this.editHistory.push({
        command: "str_replace",
        path: filePath,
        old_str: oldStr,
        new_str: newStr,
      });

      // Generate diff output
      const oldLines = content.split("\n");
      const newLines = newContent.split("\n");
      const diff = this.generateDiff(oldLines, newLines, filePath);

      return {
        success: true,
        output: diff,
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Error replacing text in ${filePath}: ${error.message}`,
      };
    }
  }

  async create(filePath: string, content: string): Promise<ToolResult> {
    try {
      const resolvedPath = path.resolve(filePath);

      // Check if user has already accepted file operations for this session
      const sessionFlags = this.confirmationService.getSessionFlags();
      if (!sessionFlags.fileOperations && !sessionFlags.allOperations) {
        // Create a diff-style preview for file creation
        const contentLines = content.split("\n");
        const diffContent = [
          `Created ${filePath}`,
          `--- /dev/null`,
          `+++ b/${filePath}`,
          `@@ -0,0 +1,${contentLines.length} @@`,
          ...contentLines.map((line) => `+${line}`),
        ].join("\n");

        const confirmationResult =
          await this.confirmationService.requestConfirmation(
            {
              operation: "Write",
              filename: filePath,
              showVSCodeOpen: false,
              content: diffContent,
            },
            "file"
          );

        if (!confirmationResult.confirmed) {
          return {
            success: false,
            error:
              confirmationResult.feedback || "File creation cancelled by user",
          };
        }
      }

      const dir = path.dirname(resolvedPath);
      await fs.ensureDir(dir);
      await fs.writeFile(resolvedPath, content, "utf-8");

      this.editHistory.push({
        command: "create",
        path: filePath,
        content,
      });

      // Generate diff output using the same method as str_replace
      const oldLines: string[] = []; // Empty for new files
      const newLines = content.split("\n");
      const diff = this.generateDiff(oldLines, newLines, filePath);

      return {
        success: true,
        output: diff,
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Error creating ${filePath}: ${error.message}`,
      };
    }
  }

  async insert(
    filePath: string,
    insertLine: number,
    content: string
  ): Promise<ToolResult> {
    try {
      const resolvedPath = path.resolve(filePath);

      if (!(await fs.pathExists(resolvedPath))) {
        return {
          success: false,
          error: `File not found: ${filePath}`,
        };
      }

      const fileContent = await fs.readFile(resolvedPath, "utf-8");
      const lines = fileContent.split("\n");

      lines.splice(insertLine - 1, 0, content);
      const newContent = lines.join("\n");

      await fs.writeFile(resolvedPath, newContent, "utf-8");

      this.editHistory.push({
        command: "insert",
        path: filePath,
        insert_line: insertLine,
        content,
      });

      return {
        success: true,
        output: `Successfully inserted content at line ${insertLine} in ${filePath}`,
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Error inserting content in ${filePath}: ${error.message}`,
      };
    }
  }

  async undoEdit(): Promise<ToolResult> {
    if (this.editHistory.length === 0) {
      return {
        success: false,
        error: "No edits to undo",
      };
    }

    const lastEdit = this.editHistory.pop()!;

    try {
      switch (lastEdit.command) {
        case "str_replace":
          if (lastEdit.path && lastEdit.old_str && lastEdit.new_str) {
            const content = await fs.readFile(lastEdit.path, "utf-8");
            const revertedContent = content.replace(
              lastEdit.new_str,
              lastEdit.old_str
            );
            await fs.writeFile(lastEdit.path, revertedContent, "utf-8");
          }
          break;

        case "create":
          if (lastEdit.path) {
            await fs.remove(lastEdit.path);
          }
          break;

        case "insert":
          if (lastEdit.path && lastEdit.insert_line) {
            const content = await fs.readFile(lastEdit.path, "utf-8");
            const lines = content.split("\n");
            lines.splice(lastEdit.insert_line - 1, 1);
            await fs.writeFile(lastEdit.path, lines.join("\n"), "utf-8");
          }
          break;
      }

      return {
        success: true,
        output: `Successfully undid ${lastEdit.command} operation`,
      };
    } catch (error: any) {
      return {
        success: false,
        error: `Error undoing edit: ${error.message}`,
      };
    }
  }

  private generateDiff(
    oldLines: string[],
    newLines: string[],
    filePath: string
  ): string {
    // Count actual changes
    let addedLines = 0;
    let removedLines = 0;
    let i = 0,
      j = 0;

    // Simple algorithm to detect changes
    while (i < oldLines.length || j < newLines.length) {
      if (
        i < oldLines.length &&
        j < newLines.length &&
        oldLines[i] === newLines[j]
      ) {
        i++;
        j++;
      } else if (
        i < oldLines.length &&
        (j >= newLines.length || oldLines[i] !== newLines[j])
      ) {
        removedLines++;
        i++;
      } else if (j < newLines.length) {
        addedLines++;
        j++;
      }
    }

    let summary = `Updated ${filePath}`;
    if (addedLines > 0 && removedLines > 0) {
      summary += ` with ${addedLines} addition${
        addedLines !== 1 ? "s" : ""
      } and ${removedLines} removal${removedLines !== 1 ? "s" : ""}`;
    } else if (addedLines > 0) {
      summary += ` with ${addedLines} addition${addedLines !== 1 ? "s" : ""}`;
    } else if (removedLines > 0) {
      summary += ` with ${removedLines} removal${
        removedLines !== 1 ? "s" : ""
      }`;
    } else {
      summary += " with changes";
    }

    // Generate proper git-style diff format
    let diff = summary + "\n";
    diff += `--- a/${filePath}\n`;
    diff += `+++ b/${filePath}\n`;
    diff += `@@ -1,${oldLines.length} +1,${newLines.length} @@\n`;

    // Generate unified diff
    i = 0;
    j = 0;
    const CONTEXT_LINES = 3;

    while (i < oldLines.length || j < newLines.length) {
      const oldLine = i < oldLines.length ? oldLines[i] : null;
      const newLine = j < newLines.length ? newLines[j] : null;

      if (oldLine === newLine && oldLine !== null) {
        // Context line
        diff += ` ${oldLine}\n`;
        i++;
        j++;
      } else {
        // Show removed lines
        if (oldLine !== null) {
          diff += `-${oldLine}\n`;
          i++;
        }
        // Show added lines
        if (newLine !== null) {
          diff += `+${newLine}\n`;
          j++;
        }
      }
    }

    return diff.trim();
  }

  getEditHistory(): EditorCommand[] {
    return [...this.editHistory];
  }
}

import { ToolResult } from '../types';

interface TodoItem {
  id: string;
  content: string;
  status: 'pending' | 'in_progress' | 'completed';
  priority: 'high' | 'medium' | 'low';
}

export class TodoTool {
  private todos: TodoItem[] = [];

  formatTodoList(): string {
    if (this.todos.length === 0) {
      return 'No todos created yet';
    }

    const getCheckbox = (status: string): string => {
      switch (status) {
        case 'completed':
          return '‚óè';
        case 'in_progress':
          return '‚óê';
        case 'pending':
          return '‚óã';
        default:
          return '‚óã';
      }
    };

    const getStatusColor = (status: string): string => {
      switch (status) {
        case 'completed':
          return '\x1b[32m'; // Green
        case 'in_progress':
          return '\x1b[36m'; // Cyan
        case 'pending':
          return '\x1b[37m'; // White/default
        default:
          return '\x1b[0m'; // Reset
      }
    };

    const reset = '\x1b[0m';
    let output = '';

    this.todos.forEach((todo, index) => {
      const checkbox = getCheckbox(todo.status);
      const statusColor = getStatusColor(todo.status);
      const strikethrough = todo.status === 'completed' ? '\x1b[9m' : '';
      const indent = index === 0 ? '' : '  ';

      output += `${indent}${statusColor}${strikethrough}${checkbox} ${todo.content}${reset}\n`;
    });

    return output;
  }

  async createTodoList(todos: TodoItem[]): Promise<ToolResult> {
    try {
      // Validate todos
      for (const todo of todos) {
        if (!todo.id || !todo.content || !todo.status || !todo.priority) {
          return {
            success: false,
            error: 'Each todo must have id, content, status, and priority fields'
          };
        }

        if (!['pending', 'in_progress', 'completed'].includes(todo.status)) {
          return {
            success: false,
            error: `Invalid status: ${todo.status}. Must be pending, in_progress, or completed`
          };
        }

        if (!['high', 'medium', 'low'].includes(todo.priority)) {
          return {
            success: false,
            error: `Invalid priority: ${todo.priority}. Must be high, medium, or low`
          };
        }
      }

      this.todos = todos;

      return {
        success: true,
        output: this.formatTodoList()
      };
    } catch (error) {
      return {
        success: false,
        error: `Error creating todo list: ${error instanceof Error ? error.message : String(error)}`
      };
    }
  }

  async updateTodoList(updates: { id: string; status?: string; content?: string; priority?: string }[]): Promise<ToolResult> {
    try {
      const updatedIds: string[] = [];

      for (const update of updates) {
        const todoIndex = this.todos.findIndex(t => t.id === update.id);

        if (todoIndex === -1) {
          return {
            success: false,
            error: `Todo with id ${update.id} not found`
          };
        }

        const todo = this.todos[todoIndex];

        if (update.status && !['pending', 'in_progress', 'completed'].includes(update.status)) {
          return {
            success: false,
            error: `Invalid status: ${update.status}. Must be pending, in_progress, or completed`
          };
        }

        if (update.priority && !['high', 'medium', 'low'].includes(update.priority)) {
          return {
            success: false,
            error: `Invalid priority: ${update.priority}. Must be high, medium, or low`
          };
        }

        if (update.status) todo.status = update.status as any;
        if (update.content) todo.content = update.content;
        if (update.priority) todo.priority = update.priority as any;

        updatedIds.push(update.id);
      }

      return {
        success: true,
        output: this.formatTodoList()
      };
    } catch (error) {
      return {
        success: false,
        error: `Error updating todo list: ${error instanceof Error ? error.message : String(error)}`
      };
    }
  }

  async viewTodoList(): Promise<ToolResult> {
    return {
      success: true,
      output: this.formatTodoList()
    };
  }
}

export interface ToolResult {
  success: boolean;
  output?: string;
  error?: string;
  data?: any;
  metadata?: {
    userSummary?: string;
    query?: string;
    [key: string]: any;
  };
}

export interface Tool {
  name: string;
  description: string;
  execute: (...args: any[]) => Promise<ToolResult>;
}

export interface EditorCommand {
  command: 'view' | 'str_replace' | 'create' | 'insert' | 'undo_edit';
  path?: string;
  old_str?: string;
  new_str?: string;
  content?: string;
  insert_line?: number;
  view_range?: [number, number];
}

export interface AgentState {
  currentDirectory: string;
  editHistory: EditorCommand[];
  tools: Tool[];
}

export interface ConfirmationState {
  skipThisSession: boolean;
  pendingOperation: boolean;
}

export enum AgentMode {
  PLAN = 'plan',
  CHILL = 'chill', 
  GIGA = 'giga'
}

export interface ModeConfig {
  mode: AgentMode;
  allowExpertModels: boolean;
  requireConfirmation: boolean;
  displayName: string;
  description: string;
}

import React, { useState, useEffect } from 'react';
import { Box, Text, useInput, useApp } from 'ink';
import { Agent } from '../agent';
import { ToolResult, AgentMode } from '../types';
import { ConfirmationService, ConfirmationOptions } from '../utils/confirmation-service';
import ConfirmationDialog from './components/confirmation-dialog';
import { modeManager } from '../utils/mode-manager';
import chalk from 'chalk';

interface Props {
  agent: Agent;
}

export default function App({ agent }: Props) {
  const [input, setInput] = useState('');
  const [history, setHistory] = useState<Array<{ command: string; result: ToolResult }>>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [confirmationOptions, setConfirmationOptions] = useState<ConfirmationOptions | null>(null);
  const [currentMode, setCurrentMode] = useState<AgentMode>(modeManager.getCurrentMode());
  const { exit } = useApp();

  const confirmationService = ConfirmationService.getInstance();

  useEffect(() => {
    const handleConfirmationRequest = (options: ConfirmationOptions) => {
      setConfirmationOptions(options);
    };

    confirmationService.on('confirmation-requested', handleConfirmationRequest);

    return () => {
      confirmationService.off('confirmation-requested', handleConfirmationRequest);
    };
  }, [confirmationService]);

  // Reset confirmation service session on app start
  useEffect(() => {
    confirmationService.resetSession();
  }, []);

  useInput(async (inputChar: string, key: any) => {
    // If confirmation dialog is open, don't handle normal input
    if (confirmationOptions) {
      return;
    }

    // Handle Shift+Tab for mode cycling (Note: This is handled in ChatInterface now)
    if (key.shift && key.tab) {
      return;
    }

    if (key.ctrl && inputChar === 'c') {
      exit();
      return;
    }

    if (key.return) {
      if (input.trim() === 'exit' || input.trim() === 'quit') {
        exit();
        return;
      }

      if (input.trim()) {
        setIsProcessing(true);
        const result = await agent.processCommand(input.trim());
        setHistory(prev => [...prev, { command: input.trim(), result }]);
        setInput('');
        setIsProcessing(false);
      }
      return;
    }

    if (key.backspace || key.delete) {
      setInput(prev => prev.slice(0, -1));
      return;
    }

    if (inputChar && !key.ctrl && !key.meta) {
      setInput(prev => prev + inputChar);
    }
  });

  const renderResult = (result: ToolResult) => {
    if (result.success) {
      return (
        <Box flexDirection="column" marginBottom={1}>
          <Text color="green">‚úì Success</Text>
          {result.output && (
            <Box marginLeft={2}>
              <Text>{result.output}</Text>
            </Box>
          )}
        </Box>
      );
    } else {
      return (
        <Box flexDirection="column" marginBottom={1}>
          <Text color="red">‚úó Error</Text>
          {result.error && (
            <Box marginLeft={2}>
              <Text color="red">{result.error}</Text>
            </Box>
          )}
        </Box>
      );
    }
  };

  const handleConfirmation = (dontAskAgain?: boolean) => {
    confirmationService.confirmOperation(true, dontAskAgain);
    setConfirmationOptions(null);
  };

  const handleRejection = (feedback?: string) => {
    confirmationService.rejectOperation(feedback);
    setConfirmationOptions(null);
  };

  if (confirmationOptions) {
    return (
      <ConfirmationDialog
        operation={confirmationOptions.operation}
        filename={confirmationOptions.filename}
        showVSCodeOpen={confirmationOptions.showVSCodeOpen}
        onConfirm={handleConfirmation}
        onReject={handleRejection}
      />
    );
  }

  return (
    <Box flexDirection="column" padding={1}>
      <Box marginBottom={1} flexDirection="column">
        <Text bold color="cyan">
          üîß Grok CLI - Text Editor Agent
        </Text>
        <Box marginTop={1}>
          <Text bold color={currentMode === AgentMode.GIGA ? 'yellow' : currentMode === AgentMode.CHILL ? 'green' : 'blue'}>
            {modeManager.getModeDisplayName()}
          </Text>
          <Text dimColor> - {modeManager.getModeDescription()}</Text>
        </Box>
      </Box>

      <Box flexDirection="column" marginBottom={1}>
        <Text dimColor>
          Available commands: view, str_replace, create, insert, undo_edit, bash, help
        </Text>
        <Text dimColor>
          Type 'help' for detailed usage, 'exit' or Ctrl+C to quit, Shift+Tab to cycle modes
        </Text>
      </Box>

      <Box flexDirection="column" marginBottom={1}>
        {history.slice(-10).map((entry, index) => (
          <Box key={index} flexDirection="column" marginBottom={1}>
            <Box>
              <Text color="blue">$ </Text>
              <Text>{entry.command}</Text>
            </Box>
            {renderResult(entry.result)}
          </Box>
        ))}
      </Box>

      <Box>
        <Text color="blue">$ </Text>
        <Text>
          {input}
          {!isProcessing && <Text color="white">‚ñà</Text>}
        </Text>
        {isProcessing && <Text color="yellow"> (processing...)</Text>}
      </Box>
    </Box>
  );
}

import React, { useState } from "react";
import { Box, Text, useInput } from "ink";

interface AddMcpServerProps {
  onClose: () => void;
  onAddServer: (name: string, command: string, args?: string[], env?: Record<string, string>, description?: string) => void;
}

type FormField = 'name' | 'command' | 'args' | 'env' | 'description';

export default function AddMcpServer({ onClose, onAddServer }: AddMcpServerProps) {
  const [currentField, setCurrentField] = useState<FormField>('name');
  const [formData, setFormData] = useState({
    name: '',
    command: '',
    args: '',
    env: '',
    description: ''
  });

  const fields: { key: FormField; label: string; placeholder: string; required: boolean }[] = [
    { key: 'name', label: 'Server Name', placeholder: 'my-server', required: true },
    { key: 'command', label: 'Command', placeholder: 'npx @modelcontextprotocol/server-filesystem', required: true },
    { key: 'args', label: 'Arguments', placeholder: '/path/to/directory (space-separated)', required: false },
    { key: 'env', label: 'Environment', placeholder: 'KEY1=value1 KEY2=value2', required: false },
    { key: 'description', label: 'Description', placeholder: 'Optional description', required: false }
  ];

  const currentFieldIndex = fields.findIndex(f => f.key === currentField);

  useInput((inputChar, key) => {
    if (key.ctrl && inputChar === "c") {
      onClose();
      return;
    }

    if (key.escape) {
      onClose();
      return;
    }

    if (key.upArrow) {
      const newIndex = currentFieldIndex === 0 ? fields.length - 1 : currentFieldIndex - 1;
      setCurrentField(fields[newIndex].key);
      return;
    }

    if (key.downArrow) {
      const newIndex = (currentFieldIndex + 1) % fields.length;
      setCurrentField(fields[newIndex].key);
      return;
    }

    if (key.tab) {
      const newIndex = (currentFieldIndex + 1) % fields.length;
      setCurrentField(fields[newIndex].key);
      return;
    }

    if (key.return) {
      // If we're on the last field or have required fields filled, submit
      if (currentFieldIndex === fields.length - 1 || 
          (formData.name.trim() && formData.command.trim())) {
        handleSubmit();
      } else {
        // Move to next field
        const newIndex = (currentFieldIndex + 1) % fields.length;
        setCurrentField(fields[newIndex].key);
      }
      return;
    }

    if (key.backspace || key.delete) {
      setFormData(prev => ({
        ...prev,
        [currentField]: prev[currentField].slice(0, -1)
      }));
      return;
    }

    if (inputChar && !key.ctrl && !key.meta) {
      setFormData(prev => ({
        ...prev,
        [currentField]: prev[currentField] + inputChar
      }));
      return;
    }
  });

  const handleSubmit = () => {
    if (!formData.name.trim() || !formData.command.trim()) {
      return; // Don't submit if required fields are empty
    }

    const args = formData.args.trim() ? formData.args.trim().split(' ') : undefined;
    const env = formData.env.trim() ? parseEnvString(formData.env.trim()) : undefined;
    const description = formData.description.trim() || undefined;

    onAddServer(formData.name.trim(), formData.command.trim(), args, env, description);
  };

  const parseEnvString = (envStr: string): Record<string, string> => {
    const env: Record<string, string> = {};
    const pairs = envStr.split(' ');

    for (const pair of pairs) {
      const [key, ...valueParts] = pair.split('=');
      if (key && valueParts.length > 0) {
        env[key] = valueParts.join('=');
      }
    }

    return env;
  };

  const canSubmit = formData.name.trim() && formData.command.trim();

  return (
    <Box flexDirection="column" paddingX={2} paddingY={1}>
      <Text color="yellow">‚ûï Add MCP Server</Text>
      <Box marginBottom={1}>
        <Text color="gray">Fill in the server configuration:</Text>
      </Box>

      <Box flexDirection="column" marginBottom={1}>
        {fields.map((field) => {
          const isSelected = field.key === currentField;
          const value = formData[field.key];

          return (
            <Box 
              key={field.key}
              borderStyle="round" 
              borderColor={isSelected ? "blue" : "gray"} 
              paddingX={1} 
              marginBottom={1}
            >
              <Box width={15}>
                <Text color={isSelected ? "blue" : "white"}>
                  {field.label}{field.required ? "*" : ""}:
                </Text>
              </Box>
              <Box flexGrow={1}>
                <Text color={value ? "white" : "gray"}>
                  {value || field.placeholder}
                  {isSelected ? "‚ñà" : ""}
                </Text>
              </Box>
            </Box>
          );
        })}
      </Box>

      <Box marginBottom={1}>
        <Text color={canSubmit ? "green" : "red"}>
          {canSubmit ? "‚úì Ready to add server" : "‚ùå Name and command are required"}
        </Text>
      </Box>

      <Box flexDirection="column" marginTop={1}>
        <Text color="gray" dimColor>‚Ä¢ Use ‚Üë/‚Üì arrows or Tab to navigate fields</Text>
        <Text color="gray" dimColor>‚Ä¢ Type to edit current field</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Enter to {canSubmit ? "add server" : "go to next field"}</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Esc to cancel</Text>
      </Box>
    </Box>
  );
}

import React, { useState, useEffect } from "react";
import { Box, Text, useInput } from "ink";
import { loadApiKeys } from "../../utils/api-keys";
import { ProviderName } from "../../utils/provider-models";
import { fetchModelsWithFallback } from "../../utils/dynamic-model-fetcher";
import { fuzzySearch } from "../../utils/fuzzy-search";

interface Provider {
  name: string;
  keyName: string;
  description: string;
}

interface AddModelProps {
  providers: Provider[];
  onClose: () => void;
  onAddModel: (providerName: string, modelName: string) => void;
}

export default function AddModel({ 
  providers, 
  onClose,
  onAddModel 
}: AddModelProps) {
  const [availableProviders, setAvailableProviders] = useState<Provider[]>([]);
  const [stage, setStage] = useState<'provider' | 'model'>('provider');
  const [selectedProviderIndex, setSelectedProviderIndex] = useState(0);
  const [selectedModelIndex, setSelectedModelIndex] = useState(0);
  const [currentSearchQuery, setCurrentSearchQuery] = useState("");
  const [filteredModels, setFilteredModels] = useState<string[]>([]);
  const [isLoadingModels, setIsLoadingModels] = useState(false);
  const [allModels, setAllModels] = useState<string[]>([]);

  useEffect(() => {
    initializeProviders();
  }, []);

  useEffect(() => {
    if (stage === 'model' && availableProviders[selectedProviderIndex] && !isLoadingModels) {
      updateFilteredModels();
    }
  }, [stage, selectedProviderIndex, currentSearchQuery, availableProviders, allModels]);

  const initializeProviders = () => {
    const apiKeys = loadApiKeys();
    const providersWithKeys: Provider[] = [];

    for (const provider of providers) {
      const keyValue = (apiKeys as any)[provider.keyName];
      // Ollama always has a base URL (defaults to localhost), so it's always available
      if (keyValue || provider.keyName === 'ollamaBaseUrl') {
        providersWithKeys.push(provider);
      }
    }

    setAvailableProviders(providersWithKeys);
  };

  const loadModelsForProvider = async (provider: Provider) => {
    setIsLoadingModels(true);
    setAllModels([]);

    const apiKeys = loadApiKeys();
    const apiKey = (apiKeys as any)[provider.keyName];
    const providerName = provider.name.toLowerCase() as ProviderName;

    try {
      const models = await fetchModelsWithFallback(providerName, apiKey);
      setAllModels(models);
    } catch (error) {
      console.error(`Failed to load models for ${provider.name}:`, error);
      setAllModels([]);
    } finally {
      setIsLoadingModels(false);
    }
  };

  const updateFilteredModels = () => {
    if (!currentSearchQuery.trim()) {
      setFilteredModels(allModels);
      return;
    }

    const filtered = fuzzySearch(
      currentSearchQuery,
      allModels,
      (model) => model,
      50 // Show up to 50 results
    );

    setFilteredModels(filtered);
  };

  useInput((inputChar, key) => {
    if (key.ctrl && inputChar === "c") {
      onClose();
      return;
    }

    if (key.escape) {
      if (stage === 'model') {
        // Go back to provider selection
        setStage('provider');
        setCurrentSearchQuery("");
        setSelectedModelIndex(0);
      } else {
        onClose();
      }
      return;
    }

    if (stage === 'provider') {
      if (key.upArrow) {
        setSelectedProviderIndex(prev => prev === 0 ? availableProviders.length - 1 : prev - 1);
        return;
      }
      if (key.downArrow) {
        setSelectedProviderIndex(prev => (prev + 1) % availableProviders.length);
        return;
      }
      if (key.return) {
        if (availableProviders.length > 0) {
          const selectedProvider = availableProviders[selectedProviderIndex];
          // Move to model selection stage and load models
          setStage('model');
          setCurrentSearchQuery("");
          setSelectedModelIndex(0);
          loadModelsForProvider(selectedProvider);
        }
        return;
      }
    } else if (stage === 'model') {
      if (key.upArrow) {
        setSelectedModelIndex(prev => prev === 0 ? Math.max(0, filteredModels.length - 1) : prev - 1);
        return;
      }
      if (key.downArrow) {
        setSelectedModelIndex(prev => filteredModels.length === 0 ? 0 : (prev + 1) % Math.min(10, filteredModels.length));
        return;
      }
      if (key.return) {
        if (filteredModels.length > 0 && selectedModelIndex < filteredModels.length) {
          const selectedModel = filteredModels[selectedModelIndex];
          const selectedProvider = availableProviders[selectedProviderIndex];
          onAddModel(selectedProvider.name, selectedModel);
        }
        return;
      }
      if (key.backspace || key.delete) {
        setCurrentSearchQuery(prev => prev.slice(0, -1));
        setSelectedModelIndex(0);
        return;
      }
      if (inputChar && !key.ctrl && !key.meta) {
        setCurrentSearchQuery(prev => prev + inputChar);
        setSelectedModelIndex(0);
        return;
      }
    }
  });

  if (availableProviders.length === 0) {
    return (
      <Box flexDirection="column" paddingX={2} paddingY={1}>
        <Text color="red">‚ùå No API keys found</Text>
        <Text color="gray">Configure API keys in /providers first</Text>
        <Box marginTop={1}>
          <Text color="gray" dimColor>Press Esc to close</Text>
        </Box>
      </Box>
    );
  }

  if (stage === 'provider') {
    return (
      <Box flexDirection="column" paddingX={2} paddingY={1}>
        <Text color="yellow">üîç Select Provider</Text>
        <Box marginBottom={1}>
          <Text color="gray">Choose a provider to browse models from:</Text>
        </Box>

        <Box flexDirection="column" marginBottom={1}>
          {availableProviders.map((provider, index) => {
            const isSelected = index === selectedProviderIndex;

            return (
              <Box 
                key={provider.keyName}
                borderStyle="round" 
                borderColor={isSelected ? "blue" : "gray"} 
                paddingX={1} 
                marginBottom={1}
              >
                <Box width={12}>
                  <Text color={isSelected ? "blue" : "white"}>{provider.name}</Text>
                </Box>
                <Box flexGrow={1}>
                  <Text color="green">‚úì API key configured</Text>
                </Box>
                <Box width={25}>
                  <Text color="gray" dimColor>{provider.description}</Text>
                </Box>
              </Box>
            );
          })}
        </Box>

        <Box flexDirection="column" marginTop={1}>
          <Text color="gray" dimColor>‚Ä¢ Use ‚Üë/‚Üì arrows to navigate</Text>
          <Text color="gray" dimColor>‚Ä¢ Press Enter to select provider</Text>
          <Text color="gray" dimColor>‚Ä¢ Press Esc to cancel</Text>
        </Box>
      </Box>
    );
  }

  // Model selection stage
  const selectedProvider = availableProviders[selectedProviderIndex];

  if (isLoadingModels) {
    return (
      <Box flexDirection="column" paddingX={2} paddingY={1}>
        <Text color="yellow">üîç Add Model from {selectedProvider.name}</Text>
        <Box marginTop={2}>
          <Text color="blue">üîÑ Loading models from {selectedProvider.name}...</Text>
        </Box>
        <Box marginTop={1}>
          <Text color="gray" dimColor>Press Esc to go back</Text>
        </Box>
      </Box>
    );
  }

  return (
    <Box flexDirection="column" paddingX={2} paddingY={1}>
      <Text color="yellow">üîç Add Model from {selectedProvider.name}</Text>
      <Box marginBottom={1}>
        <Text color="gray">Search: </Text>
        <Text>{currentSearchQuery || ""}‚ñà</Text>
      </Box>

      <Box flexDirection="column" marginBottom={1}>
        {filteredModels.length === 0 ? (
          <Box borderStyle="round" borderColor="gray" paddingX={1}>
            <Text color="gray">
              {allModels.length === 0 
                ? "No models available" 
                : `No models found matching "${currentSearchQuery}"`
              }
            </Text>
          </Box>
        ) : (
          filteredModels.slice(0, 10).map((model, index) => {
            const isSelected = index === selectedModelIndex;

            return (
              <Box 
                key={model}
                borderStyle="round" 
                borderColor={isSelected ? "blue" : "gray"} 
                paddingX={1} 
                marginBottom={1}
              >
                <Text color={isSelected ? "blue" : "white"}>{model}</Text>
              </Box>
            );
          })
        )}
        {filteredModels.length > 10 && (
          <Box paddingX={1}>
            <Text color="gray" dimColor>... and {filteredModels.length - 10} more</Text>
          </Box>
        )}
      </Box>

      <Box flexDirection="column" marginTop={1}>
        <Text color="gray" dimColor>‚Ä¢ Type to search models ({allModels.length} total)</Text>
        <Text color="gray" dimColor>‚Ä¢ Use ‚Üë/‚Üì arrows to navigate</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Enter to add selected model</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Esc to go back</Text>
      </Box>
    </Box>
  );
}

import React, { useState } from "react";
import { Box, Text, useInput } from "ink";

interface AddPromptProps {
  onClose: () => void;
  onAddPrompt: (name: string, content: string) => void;
}

export default function AddPrompt({ onClose, onAddPrompt }: AddPromptProps) {
  const [stage, setStage] = useState<'name' | 'content'>('name');
  const [promptName, setPromptName] = useState("");
  const [promptContent, setPromptContent] = useState("");
  const [currentLine, setCurrentLine] = useState("");

  useInput((inputChar, key) => {
    if (key.ctrl && inputChar === "c") {
      onClose();
      return;
    }

    if (key.escape) {
      if (stage === 'content') {
        setStage('name');
        setPromptContent("");
        setCurrentLine("");
      } else {
        onClose();
      }
      return;
    }

    if (stage === 'name') {
      if (key.return) {
        if (promptName.trim()) {
          setStage('content');
        }
        return;
      }

      if (key.backspace || key.delete) {
        setPromptName(prev => prev.slice(0, -1));
        return;
      }

      if (inputChar && !key.ctrl && !key.meta) {
        setPromptName(prev => prev + inputChar);
        return;
      }
    } else if (stage === 'content') {
      if (key.ctrl && inputChar === "s") {
        // Save the prompt
        if (promptName.trim() && (promptContent.trim() || currentLine.trim())) {
          const finalContent = promptContent + (currentLine ? (promptContent ? '\n' : '') + currentLine : '');
          onAddPrompt(promptName.trim(), finalContent.trim());
        }
        return;
      }

      if (key.return) {
        setPromptContent(prev => prev + (prev ? '\n' : '') + currentLine);
        setCurrentLine("");
        return;
      }

      if (key.backspace || key.delete) {
        if (currentLine) {
          setCurrentLine(prev => prev.slice(0, -1));
        } else if (promptContent) {
          const lines = promptContent.split('\n');
          const lastLine = lines.pop() || '';
          setPromptContent(lines.join('\n'));
          setCurrentLine(lastLine);
        }
        return;
      }

      if (inputChar && !key.ctrl && !key.meta) {
        setCurrentLine(prev => prev + inputChar);
        return;
      }
    }
  });

  if (stage === 'name') {
    return (
      <Box flexDirection="column" paddingX={2} paddingY={1}>
        <Text color="yellow">‚ûï Add Custom Prompt</Text>
        <Box marginBottom={1}>
          <Text color="gray">Enter prompt name:</Text>
        </Box>

        <Box borderStyle="round" borderColor="blue" paddingX={1} marginBottom={1}>
          <Text>{promptName}‚ñà</Text>
        </Box>

        <Box flexDirection="column" marginTop={1}>
          <Text color="gray" dimColor>‚Ä¢ Type the name for your custom prompt</Text>
          <Text color="gray" dimColor>‚Ä¢ Press Enter to continue to content</Text>
          <Text color="gray" dimColor>‚Ä¢ Press Esc to cancel</Text>
        </Box>
      </Box>
    );
  }

  // Content stage
  const displayContent = promptContent + (promptContent && currentLine ? '\n' : '') + currentLine;
  const lines = displayContent.split('\n');
  const displayLines = lines.slice(-8); // Show last 8 lines
  const hasMoreLines = lines.length > 8;

  return (
    <Box flexDirection="column" paddingX={2} paddingY={1}>
      <Text color="yellow">‚ûï Add Custom Prompt: {promptName}</Text>
      <Box marginBottom={1}>
        <Text color="gray">Enter prompt content (markdown supported):</Text>
      </Box>

      <Box flexDirection="column" borderStyle="round" borderColor="blue" paddingX={1} marginBottom={1} minHeight={10}>
        {hasMoreLines && (
          <Text color="gray" dimColor>... ({lines.length - 8} more lines above)</Text>
        )}
        {displayLines.map((line, index) => (
          <Text key={index}>{line}{index === displayLines.length - 1 ? '‚ñà' : ''}</Text>
        ))}
        {displayLines.length === 0 && <Text>‚ñà</Text>}
      </Box>

      <Box flexDirection="column" marginTop={1}>
        <Text color="gray" dimColor>‚Ä¢ Type your prompt content (multi-line supported)</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Enter for new line</Text>
        <Text color="green" dimColor>‚Ä¢ Press Ctrl+S to save prompt</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Esc to go back</Text>
      </Box>
    </Box>
  );
}

import React, { useState } from "react";
import { Box, Text, useInput, useApp } from "ink";
import { GigaAgent } from "../../agent/giga-agent";
import * as fs from "fs";
import * as path from "path";
import * as os from "os";

interface ApiKeyInputProps {
  onApiKeySet: (agent: GigaAgent) => void;
}

interface UserSettings {
  apiKey?: string;
  groqApiKey?: string;
  anthropicApiKey?: string;
  openRouterApiKey?: string;
}

export default function ApiKeyInput({ onApiKeySet }: ApiKeyInputProps) {
  const [input, setInput] = useState("");
  const [groqInput, setGroqInput] = useState("");
  const [anthropicInput, setAnthropicInput] = useState("");
  const [openRouterInput, setOpenRouterInput] = useState("");
  const [currentField, setCurrentField] = useState("grok");
  const [error, setError] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { exit } = useApp();

  useInput((inputChar, key) => {
    if (isSubmitting) return;

    if (key.ctrl && inputChar === "c") {
      exit();
      return;
    }

    if (key.return) {
      if (currentField === "grok") {
        setCurrentField("groq");
      } else if (currentField === "groq") {
        setCurrentField("anthropic");
      } else if (currentField === "anthropic") {
        setCurrentField("openrouter");
      } else {
        handleSubmit();
      }
      return;
    }

    if (key.backspace || key.delete) {
      if (currentField === "grok") {
        setInput((prev) => prev.slice(0, -1));
      } else if (currentField === "groq") {
        setGroqInput((prev) => prev.slice(0, -1));
      } else if (currentField === "anthropic") {
        setAnthropicInput((prev) => prev.slice(0, -1));
      } else if (currentField === "openrouter") {
        setOpenRouterInput((prev) => prev.slice(0, -1));
      }
      setError("");
      return;
    }

    if (inputChar && !key.ctrl && !key.meta) {
      if (currentField === "grok") {
        setInput((prev) => prev + inputChar);
      } else if (currentField === "groq") {
        setGroqInput((prev) => prev + inputChar);
      } else if (currentField === "anthropic") {
        setAnthropicInput((prev) => prev + inputChar);
      } else if (currentField === "openrouter") {
        setOpenRouterInput((prev) => prev + inputChar);
      }
      setError("");
    }
  });


  const handleSubmit = async () => {
    // Check for API keys in input fields or environment variables
    const xaiKey = input.trim() || process.env.XAI_API_KEY;
    const groqKey = groqInput.trim() || process.env.GROQ_API_KEY;
    const anthropicKey = anthropicInput.trim() || process.env.ANTHROPIC_API_KEY || process.env.CLAUDE_API_KEY;
    const openRouterKey = openRouterInput.trim() || process.env.OPENROUTER_API_KEY;

    if (!xaiKey && !groqKey && !anthropicKey && !openRouterKey) {
      setError("At least one API key is required (via input or environment variable)");
      return;
    }

    setIsSubmitting(true);
    try {
      const apiKey = xaiKey;
      const groqApiKey = groqKey;
      const agent = new GigaAgent(apiKey, groqApiKey);

      // Set environment variable for current process
      process.env.XAI_API_KEY = apiKey;

      // Save to .grok/user-settings.json
      try {
        const homeDir = os.homedir();
        const gigaDir = path.join(homeDir, '.giga');
        const settingsFile = path.join(gigaDir, 'user-settings.json');

        // Create .giga directory if it doesn't exist
        if (!fs.existsSync(gigaDir)) {
          fs.mkdirSync(gigaDir, { mode: 0o700 });
        }

        // Load existing settings or create new
        let settings: UserSettings = {};
        if (fs.existsSync(settingsFile)) {
          try {
            settings = JSON.parse(fs.readFileSync(settingsFile, 'utf8'));
          } catch {
            settings = {};
          }
        }

        // Update API key
        settings.apiKey = apiKey;

        // Save settings
        fs.writeFileSync(settingsFile, JSON.stringify(settings, null, 2), { mode: 0o600 });

        console.log(`\n‚úÖ API key saved to ~/.giga/user-settings.json`);
      } catch (error) {
        console.log('\n‚ö†Ô∏è Could not save API key to settings file');
        console.log('API key set for current session only');
      }

      onApiKeySet(agent);
    } catch (error: any) {
      setError("Invalid API key format");
      setIsSubmitting(false);
    }
  };

  const displayText = input.length > 0 ? 
    (isSubmitting ? "*".repeat(input.length) : "*".repeat(input.length) + "‚ñà") : 
    (isSubmitting ? " " : "‚ñà");

  return (
    <Box flexDirection="column" paddingX={2} paddingY={1}>
      <Text color="yellow">üîë API Keys Required</Text>
      <Box marginBottom={1}>
        <Text color="gray">Please enter your API keys to continue:</Text>
      </Box>

      <Box flexDirection="column" marginBottom={1}>
        <Box borderStyle="round" borderColor={currentField === "grok" ? "blue" : "gray"} paddingX={1} marginBottom={1}>
          <Text color="gray">xAI API Key ‚ùØ </Text>
          <Text>{input.length > 0 ? (isSubmitting ? "*".repeat(input.length) : "*".repeat(input.length) + "‚ñà") : (isSubmitting ? "..." : "‚ñà")}</Text>
        </Box>
        <Box borderStyle="round" borderColor={currentField === "groq" ? "blue" : "gray"} paddingX={1} marginBottom={1}>
          <Text color="gray">Groq API Key ‚ùØ </Text>
          <Text>{groqInput.length > 0 ? (isSubmitting ? "*".repeat(groqInput.length) : "*".repeat(groqInput.length) + "‚ñà") : (isSubmitting ? "..." : "‚ñà")}</Text>
        </Box>
        <Box borderStyle="round" borderColor={currentField === "anthropic" ? "blue" : "gray"} paddingX={1} marginBottom={1}>
          <Text color="gray">Anthropic API Key ‚ùØ </Text>
          <Text>{anthropicInput.length > 0 ? (isSubmitting ? "*".repeat(anthropicInput.length) : "*".repeat(anthropicInput.length) + "‚ñà") : (isSubmitting ? "..." : "‚ñà")}</Text>
        </Box>
        <Box borderStyle="round" borderColor={currentField === "openrouter" ? "blue" : "gray"} paddingX={1} marginBottom={1}>
          <Text color="gray">OpenRouter API Key ‚ùØ </Text>
          <Text>{openRouterInput.length > 0 ? (isSubmitting ? "*".repeat(openRouterInput.length) : "*".repeat(openRouterInput.length) + "‚ñà") : (isSubmitting ? "..." : "‚ñà")}</Text>
        </Box>
      </Box>

      {error ? (
        <Box marginBottom={1}>
          <Text color="red">‚ùå {error}</Text>
        </Box>
      ) : null}

      <Box flexDirection="column" marginTop={1}>
        <Text color="gray" dimColor>‚Ä¢ Press Enter to move to next field or submit</Text>
        <Text color="gray" dimColor>‚Ä¢ Press Ctrl+C to exit</Text>
        <Text color="gray" dimColor>Note: API keys will be saved to ~/.giga/user-settings.json</Text>
      </Box>

      {isSubmitting ? (
        <Box marginTop={1}>
          <Text color="yellow">üîÑ Validating API keys...</Text>
        </Box>
      ) : null}
    </Box>
  );
}

import React from "react";
import { Box, Text } from "ink";
import { ChatEntry } from "../../agent/giga-agent";
import { DiffRenderer } from "./diff-renderer";
import { MarkdownRenderer } from "../utils/markdown-renderer";

interface ChatHistoryProps {
  entries: ChatEntry[];
}

export function ChatHistory({ entries }: ChatHistoryProps) {
  const renderDiff = (diffContent: string, filename?: string) => {
    return (
      <DiffRenderer
        diffContent={diffContent}
        filename={filename}
        terminalWidth={80}
      />
    );
  };

  const renderFileContent = (content: string) => {
    const lines = content.split("\n");

    // Calculate minimum indentation like DiffRenderer does
    let baseIndentation = Infinity;
    for (const line of lines) {
      if (line.trim() === "") continue;
      const firstCharIndex = line.search(/\S/);
      const currentIndent = firstCharIndex === -1 ? 0 : firstCharIndex;
      baseIndentation = Math.min(baseIndentation, currentIndent);
    }
    if (!isFinite(baseIndentation)) {
      baseIndentation = 0;
    }

    return lines.map((line, index) => {
      const displayContent = line.substring(baseIndentation);
      return (
        <Text key={index} color="gray">
          {displayContent}
        </Text>
      );
    });
  };

  const renderChatEntry = (entry: ChatEntry, index: number) => {
    switch (entry.type) {
      case "user":
        return (
          <Box key={index} flexDirection="column" marginTop={1}>
            <Box>
              <Text color="gray">
                {">"} {entry.content}
              </Text>
            </Box>
          </Box>
        );

      case "assistant":
        return (
          <Box key={index} flexDirection="column" marginTop={1}>
            <Box flexDirection="row" alignItems="flex-start">
              <Text color="white">‚è∫ </Text>
              <Box flexDirection="column" flexGrow={1}>
                {entry.toolCalls ? (
                  // If there are tool calls, just show plain text
                  <Text color="white">{entry.content.trim()}</Text>
                ) : (
                  // If no tool calls, render as markdown
                  <MarkdownRenderer content={entry.content.trim()} />
                )}
                {entry.isStreaming && <Text color="cyan">‚ñà</Text>}
                {entry.metrics && !entry.isStreaming && (
                  <Box marginTop={1}>
                    <Text color="blue">prefill - {entry.metrics.prefillTimeMs}ms</Text>
                    <Text color="white"> | </Text>
                    <Text color="yellow">decode - {entry.metrics.tokensPerSecond} toks/sec ({entry.metrics.outputTokens} out / {entry.metrics.decodeTimeMs}ms)</Text>
                  </Box>
                )}
              </Box>
            </Box>
          </Box>
        );

      case "tool_result":
        const getToolActionName = (toolName: string) => {
          switch (toolName) {
            case "view_file":
              return "Read";
            case "str_replace_editor":
              return "Update";
            case "create_file":
              return "Create";
            case "bash":
              return "Bash";
            case "create_todo_list":
              return "Created Todo";
            case "update_todo_list":
              return "Updated Todo";
            case "perplexity_search":
              return "Search";
            default:
              return "Tool";
          }
        };

        const getToolFilePath = (toolCall: any) => {
          if (toolCall?.function?.arguments) {
            try {
              const args = JSON.parse(toolCall.function.arguments);
              // Handle todo tools and search tools specially - they don't have file paths
              if (
                toolCall.function.name === "create_todo_list" ||
                toolCall.function.name === "update_todo_list" ||
                toolCall.function.name === "perplexity_search"
              ) {
                if (toolCall.function.name === "perplexity_search") {
                  return `"${args.query || 'unknown query'}"`;
                }
                return "";
              }
              return args.path || args.file_path || args.command || "unknown";
            } catch {
              return "unknown";
            }
          }
          return "unknown";
        };

        const toolName = entry.toolCall?.function?.name || "unknown";
        const actionName = getToolActionName(toolName);
        const filePath = getToolFilePath(entry.toolCall);


        const shouldShowDiff =
          toolName === "str_replace_editor" || toolName === "create_file";
        const shouldShowFileContent = toolName === "view_file";
        const shouldShowSearchResult = toolName === "perplexity_search";

        return (
          <Box key={index} flexDirection="column" marginTop={1}>
            <Box>
              <Text color="magenta">‚è∫</Text>
              <Text color="white">
                {" "}
                {filePath ? `${actionName}(${filePath})` : actionName}
              </Text>
            </Box>
            <Box marginLeft={2} flexDirection="column">
              {shouldShowFileContent ? (
                <Box flexDirection="column">
                  <Text color="gray">‚éø File contents:</Text>
                  <Box marginLeft={2} flexDirection="column">
                    {renderFileContent(entry.content)}
                  </Box>
                </Box>
              ) : shouldShowSearchResult ? (
                <Box flexDirection="column">
                  <Text color="gray">‚éø {entry.toolResult?.metadata?.userSummary || 'Search completed'}</Text>
                </Box>
              ) : shouldShowDiff ? (
                // For diff results, show only the summary line, not the raw content
                <Text color="gray">‚éø {entry.content.split('\n')[0]}</Text>
              ) : (
                <Text color="gray">‚éø {entry.content}</Text>
              )}
            </Box>
            {shouldShowDiff && (
              <Box marginLeft={4} flexDirection="column">
                {renderDiff(entry.content, filePath)}
              </Box>
            )}
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Box flexDirection="column">{entries.slice(-20).map(renderChatEntry)}</Box>
  );
}

