import React from "react";
import { Box, Text } from "ink";
import { fuzzySearch } from "../../utils/fuzzy-search";

interface CommandSuggestion {
  command: string;
  description: string;
}

interface CommandSuggestionsProps {
  suggestions: CommandSuggestion[];
  input: string;
  selectedIndex: number;
  isVisible: boolean;
}

export function CommandSuggestions({
  suggestions,
  input,
  selectedIndex,
  isVisible,
}: CommandSuggestionsProps) {
  if (!isVisible) return null;

  const filteredSuggestions = input.startsWith("/")
    ? fuzzySearch(
        input.substring(1), // Remove the "/" for fuzzy matching
        suggestions.filter(s => s.command.startsWith("/")),
        (suggestion) => suggestion.command.substring(1), // Remove "/" for matching
        8
      )
    : fuzzySearch(
        input,
        suggestions,
        (suggestion) => suggestion.command,
        8
      );

  return (
    <Box marginTop={1} flexDirection="column">
      {filteredSuggestions.map((suggestion, index) => (
        <Box key={index} paddingLeft={1}>
          <Text
            color={index === selectedIndex ? "black" : "white"}
            backgroundColor={index === selectedIndex ? "cyan" : undefined}
          >
            {suggestion.command}
          </Text>
          <Box marginLeft={1}>
            <Text color="gray">{suggestion.description}</Text>
          </Box>
        </Box>
      ))}
      <Box marginTop={1}>
        <Text color="gray" dimColor>
          ‚Üë‚Üì navigate ‚Ä¢ Enter/Tab select ‚Ä¢ Esc cancel
        </Text>
      </Box>
    </Box>
  );
}

import React, { useState } from "react";
import { Box, Text, useInput } from "ink";
import { DiffRenderer } from "./diff-renderer";

interface ConfirmationDialogProps {
  operation: string;
  filename: string;
  onConfirm: (dontAskAgain?: boolean) => void;
  onReject: (feedback?: string) => void;
  showVSCodeOpen?: boolean;
  content?: string; // Optional content to show (file content or command)
}

export default function ConfirmationDialog({
  operation,
  filename,
  onConfirm,
  onReject,
  showVSCodeOpen = false,
  content,
}: ConfirmationDialogProps) {
  const [selectedOption, setSelectedOption] = useState(0);
  const [feedbackMode, setFeedbackMode] = useState(false);
  const [feedback, setFeedback] = useState("");

  const options = [
    "Yes",
    "Yes, and don't ask again this session",
    "No",
    "No, with feedback",
  ];

  useInput((input, key) => {
    if (feedbackMode) {
      if (key.return) {
        onReject(feedback.trim());
        return;
      }
      if (key.backspace || key.delete) {
        setFeedback((prev) => prev.slice(0, -1));
        return;
      }
      if (input && !key.ctrl && !key.meta) {
        setFeedback((prev) => prev + input);
      }
      return;
    }

    if (key.upArrow || (key.shift && key.tab)) {
      setSelectedOption((prev) => (prev > 0 ? prev - 1 : options.length - 1));
      return;
    }

    if (key.downArrow || key.tab) {
      setSelectedOption((prev) => (prev + 1) % options.length);
      return;
    }

    if (key.return) {
      if (selectedOption === 0) {
        onConfirm(false);
      } else if (selectedOption === 1) {
        onConfirm(true);
      } else if (selectedOption === 2) {
        onReject("Operation cancelled by user");
      } else {
        setFeedbackMode(true);
      }
      return;
    }

    if (key.escape) {
      if (feedbackMode) {
        setFeedbackMode(false);
        setFeedback("");
      } else {
        // Cancel the confirmation when escape is pressed from main confirmation
        onReject("Operation cancelled by user (pressed Escape)");
      }
      return;
    }
  });

  if (feedbackMode) {
    return (
      <Box flexDirection="column" padding={1}>
        <Box flexDirection="column" marginBottom={1}>
          <Text color="gray">
            Type your feedback and press Enter, or press Escape to go back.
          </Text>
        </Box>

        <Box
          borderStyle="round"
          borderColor="yellow"
          paddingX={1}
          marginTop={1}
        >
          <Text color="gray">‚ùØ </Text>
          <Text>
            {feedback}
            <Text color="white">‚ñà</Text>
          </Text>
        </Box>
      </Box>
    );
  }

  return (
    <Box flexDirection="column" borderStyle="round" borderColor="yellow" padding={1}>
      {/* Compact header */}
      <Box marginBottom={1}>
        <Text color="yellow" bold>‚ö†Ô∏è  Confirm: </Text>
        <Text color="white">{operation}({filename})</Text>
      </Box>

      {/* Show VSCode notice if applicable */}
      {showVSCodeOpen && (
        <Box marginBottom={1}>
          <Text color="gray">üìù Opened in VS Code</Text>
        </Box>
      )}

      {/* Compact content preview - only show first few lines */}
      {content && (
        <Box marginBottom={1}>
          <Text color="gray" wrap="truncate">
            üìÑ {content.split('\n').slice(0, 2).join(' ').substring(0, 60)}...
          </Text>
        </Box>
      )}

      {/* Vertical confirmation options */}
      <Box flexDirection="column" marginBottom={1}>
        {options.map((option, index) => (
          <Box key={index}>
            <Text
              color={selectedOption === index ? "black" : "gray"}
              backgroundColor={selectedOption === index ? "cyan" : undefined}
            >
              [{index + 1}] {option}
            </Text>
          </Box>
        ))}
      </Box>

      <Text color="gray" dimColor>
        ‚Üë‚Üì/Tab select ‚Ä¢ Enter confirm ‚Ä¢ Esc cancel
      </Text>
    </Box>
  );
}

import React, { useState, useEffect } from 'react';
import { Box, Text, useInput } from 'ink';
import { ConversationManager, ConversationSummary } from '../../utils/conversation-manager';
import { fuzzySearch } from '../../utils/fuzzy-search';

interface ConversationHistoryProps {
  isVisible: boolean;
  onClose: () => void;
  onSelectConversation: (conversationId: string) => void;
}

export function ConversationHistory({ 
  isVisible, 
  onClose, 
  onSelectConversation 
}: ConversationHistoryProps) {
  const [conversations, setConversations] = useState<ConversationSummary[]>([]);
  const [filteredConversations, setFilteredConversations] = useState<ConversationSummary[]>([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const conversationManager = ConversationManager.getInstance();

  // Load conversations on mount
  useEffect(() => {
    if (isVisible) {
      loadConversations();
    }
  }, [isVisible]);

  // Filter conversations when search query changes
  useEffect(() => {
    if (!searchQuery.trim()) {
      setFilteredConversations(conversations);
    } else {
      const filtered = fuzzySearch(
        searchQuery,
        conversations,
        (conv) => `${conv.title} ${conv.preview}`,
        10
      );
      setFilteredConversations(filtered);
    }
    setSelectedIndex(0);
  }, [conversations, searchQuery]);

  const loadConversations = async () => {
    setIsLoading(true);
    try {
      const convList = await conversationManager.listConversations();
      setConversations(convList);
    } catch (error) {
      console.error('Failed to load conversations:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const formatDate = (date: Date): string => {
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      return 'Today';
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else if (diffDays < 30) {
      return `${Math.floor(diffDays / 7)}w ago`;
    } else {
      return date.toLocaleDateString();
    }
  };

  const handleDeleteConversation = async (conversationId: string) => {
    try {
      await conversationManager.deleteConversation(conversationId);
      await loadConversations();
    } catch (error) {
      console.error('Failed to delete conversation:', error);
    }
  };

  useInput((input, key) => {
    if (!isVisible) return;

    if (key.escape) {
      onClose();
      return;
    }

    if (key.return) {
      if (filteredConversations.length > 0 && selectedIndex < filteredConversations.length) {
        onSelectConversation(filteredConversations[selectedIndex].id);
        onClose();
      }
      return;
    }

    if (key.upArrow) {
      setSelectedIndex(prev => 
        prev > 0 ? prev - 1 : Math.max(0, filteredConversations.length - 1)
      );
      return;
    }

    if (key.downArrow) {
      setSelectedIndex(prev => 
        prev < filteredConversations.length - 1 ? prev + 1 : 0
      );
      return;
    }

    if (key.delete || key.backspace) {
      if (searchQuery.length > 0) {
        setSearchQuery(prev => prev.slice(0, -1));
      }
      return;
    }

    // Handle delete key (d) to delete conversation
    if (input === 'd' && filteredConversations.length > 0) {
      const conversation = filteredConversations[selectedIndex];
      if (conversation) {
        handleDeleteConversation(conversation.id);
      }
      return;
    }

    // Handle regular character input for search
    if (input && input.length === 1 && !key.ctrl && !key.meta) {
      setSearchQuery(prev => prev + input);
    }
  });

  if (!isVisible) {
    return null;
  }

  return (
    <Box 
      flexDirection="column" 
      width="100%" 
      height={20}
      borderStyle="round" 
      borderColor="cyan"
      padding={1}
    >
      <Box marginBottom={1}>
        <Text bold color="cyan">üìö Conversation History</Text>
      </Box>

      <Box marginBottom={1}>
        <Text color="gray">
          Search: {searchQuery}
          {searchQuery && <Text color="cyan">‚ñà</Text>}
        </Text>
      </Box>

      {isLoading ? (
        <Box justifyContent="center" alignItems="center" height={10}>
          <Text color="cyan">Loading conversations...</Text>
        </Box>
      ) : filteredConversations.length === 0 ? (
        <Box justifyContent="center" alignItems="center" height={10}>
          <Text color="gray">
            {searchQuery ? 'No matching conversations found' : 'No conversations yet'}
          </Text>
        </Box>
      ) : (
        <Box flexDirection="column">
          {filteredConversations.slice(0, 12).map((conversation, index) => {
            const isSelected = index === selectedIndex;

            return (
              <Box key={conversation.id} flexDirection="column" width="100%">
                <Box flexDirection="row" justifyContent="space-between">
                  <Text color={isSelected ? 'cyan' : 'white'} bold={isSelected}>
                    {isSelected ? '> ' : '  '}{conversation.title}
                  </Text>
                  <Text color={isSelected ? 'cyan' : 'gray'} dimColor={!isSelected}>
                    {formatDate(conversation.updatedAt)}
                  </Text>
                </Box>

                <Box flexDirection="row" justifyContent="space-between">
                  <Text color={isSelected ? 'cyan' : 'gray'} dimColor={!isSelected}>
                    {isSelected ? '  ' : '  '}{conversation.preview.slice(0, 50)}...
                  </Text>
                  <Text color={isSelected ? 'cyan' : 'gray'} dimColor={!isSelected}>
                    {conversation.messageCount} msgs | {conversation.model}
                  </Text>
                </Box>
              </Box>
            );
          })}
        </Box>
      )}

      <Box marginTop={1}>
        <Text color="gray" dimColor>
          ‚Üë‚Üì Navigate ‚Ä¢ Enter Select ‚Ä¢ d Delete ‚Ä¢ Esc Close ‚Ä¢ Type to search
        </Text>
      </Box>
    </Box>
  );
}

import React, { useState, useEffect } from "react";
import { Box, Text, useInput } from "ink";
import { loadAddedMcpServers, AddedMcpServer } from "../../utils/added-mcp-servers";

interface DeleteMcpServerProps {
  onClose: () => void;
  onDeleteServer: (name: string) => void;
}

export default function DeleteMcpServer({ onClose, onDeleteServer }: DeleteMcpServerProps) {
  const [servers, setServers] = useState<AddedMcpServer[]>([]);
  const [filteredServers, setFilteredServers] = useState<AddedMcpServer[]>([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [isMounted, setIsMounted] = useState(true);

  useEffect(() => {
    loadServers();

    return () => {
      setIsMounted(false);
    };
  }, []);

  useEffect(() => {
    updateFilteredServers();
  }, [searchQuery, servers]);

  const loadServers = () => {
    const loadedServers = loadAddedMcpServers();
    if (isMounted) {
      setServers(loadedServers);
    }
  };

  const updateFilteredServers = () => {
    if (!searchQuery.trim()) {
      setFilteredServers(servers);
      return;
    }

    const query = searchQuery.toLowerCase();
    const filtered = servers.filter(server => 
      server.name.toLowerCase().includes(query) ||
      server.command.toLowerCase().includes(query) ||
      (server.description && server.description.toLowerCase().includes(query))
    ).sort((a, b) => a.name.localeCompare(b.name));

    setFilteredServers(filtered);

    // Reset selection if current index is out of bounds
    if (selectedIndex >= filtered.length) {
      setSelectedIndex(0);
    }
  };

  useInput((inputChar, key) => {
    if (key.ctrl && inputChar === "c") {
      onClose();
      return;
    }

    if (key.escape) {
      onClose();
      return;
    }

    if (key.upArrow) {
      setSelectedIndex(prev => prev === 0 ? Math.max(0, filteredServers.length - 1) : prev - 1);
      return;
    }

    if (key.downArrow) {
      setSelectedIndex(prev => filteredServers.length === 0 ? 0 : (prev + 1) % Math.min(10, filteredServers.length));
      return;
    }

    if (key.return) {
      if (filteredServers.length > 0 && selectedIndex < filteredServers.length) {
        const selectedServer = filteredServers[selectedIndex];
        onDeleteServer(selectedServer.name);

        // Refresh servers after deletion
        setTimeout(() => {
          if (isMounted) {
            loadServers();
          }
        }, 100);
      }
      return;
    }

