// Main validation function that routes to the correct provider
export async function validateApiKey(provider: string, apiKey: string, model?: string): Promise<ApiKeyValidationResult> {
  // Special case for Ollama - it's a base URL, not an API key, and can be empty (defaults to localhost)
  if (provider.toLowerCase() === 'ollama') {
    const baseUrl = apiKey || 'http://localhost:11434';
    return validateOllamaUrl(baseUrl);
  }

  if (!apiKey || !apiKey.trim()) {
    return { isValid: false, error: 'API key is empty' };
  }

  switch (provider.toLowerCase()) {
    case 'openrouter':
      return validateOpenRouterKey(apiKey);
    case 'anthropic':
      return validateAnthropicKey(apiKey);
    case 'google':
      return validateGoogleKey(apiKey);
    case 'xai':
      return validateXaiKey(apiKey);
    case 'groq':
      return validateGroqKey(apiKey);
    case 'cerebras':
      return validateCerebrasKey(apiKey, model);
    case 'perplexity':
      return validatePerplexityKey(apiKey);
    case 'openai':
      return validateOpenaiKey(apiKey);
    default:
      return { isValid: false, error: 'Unknown provider' };
  }
}

import * as fs from "fs";
import * as path from "path";
import * as os from "os";

interface UserSettings {
  apiKey?: string;
  groqApiKey?: string;
  anthropicApiKey?: string;
  openRouterApiKey?: string;
  googleApiKey?: string;
  xaiApiKey?: string;
  cerebrasApiKey?: string;
  perplexityApiKey?: string;
  openaiApiKey?: string;
  ollamaBaseUrl?: string;
}

export interface ApiKeys {
  xaiApiKey?: string;
  groqApiKey?: string;
  anthropicApiKey?: string;
  openRouterApiKey?: string;
  googleApiKey?: string;
  cerebrasApiKey?: string;
  perplexityApiKey?: string;
  openaiApiKey?: string;
  ollamaBaseUrl?: string;
}

function checkShellFiles(): ApiKeys {
  const homeDir = os.homedir();
  const shellFiles = [
    path.join(homeDir, '.bashrc'),
    path.join(homeDir, '.zshrc'),
    path.join(homeDir, '.bash_profile'),
    path.join(homeDir, '.profile')
  ];

  const keys: ApiKeys = {};
  const keyPatterns = {
    xaiApiKey: /export\s+XAI_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    groqApiKey: /export\s+GROQ_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    anthropicApiKey: /export\s+(?:ANTHROPIC_API_KEY|CLAUDE_API_KEY)\s*=\s*['"]?([^'";\s]+)['"]?/,
    openRouterApiKey: /export\s+OPENROUTER_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    googleApiKey: /export\s+GOOGLE_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    cerebrasApiKey: /export\s+CEREBRAS_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    perplexityApiKey: /export\s+PERPLEXITY_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    openaiApiKey: /export\s+OPENAI_API_KEY\s*=\s*['"]?([^'";\s]+)['"]?/,
    ollamaBaseUrl: /export\s+OLLAMA_BASE_URL\s*=\s*['"]?([^'";\s]+)['"]?/,
  } as const;

  for (const file of shellFiles) {
    try {
      if (fs.existsSync(file)) {
        const content = fs.readFileSync(file, 'utf8');

        for (const [keyName, pattern] of Object.entries(keyPatterns)) {
          if (!keys[keyName as keyof ApiKeys]) {
            const match = content.match(pattern);
            if (match && match[1]) {
              (keys as any)[keyName] = match[1];
            }
          }
        }
      }
    } catch (error) {
      // Ignore errors reading shell files
    }
  }

  return keys;
}

function saveShellKeysToSettings(shellKeys: ApiKeys): void {
  try {
    const homeDir = os.homedir();
    const settingsDir = path.join(homeDir, '.giga');
    const settingsFile = path.join(settingsDir, 'user-settings.json');

    // Create .giga directory if it doesn't exist
    if (!fs.existsSync(settingsDir)) {
      fs.mkdirSync(settingsDir, { recursive: true });
    }

    // Load existing settings or create empty object
    let settings: UserSettings = {};
    if (fs.existsSync(settingsFile)) {
      settings = JSON.parse(fs.readFileSync(settingsFile, 'utf8'));
    }

    // Only save shell keys that are not already in settings and are not empty
    let hasChanges = false;
    const keyMappings: { [K in keyof ApiKeys]: keyof UserSettings } = {
      xaiApiKey: 'xaiApiKey',
      groqApiKey: 'groqApiKey', 
      anthropicApiKey: 'anthropicApiKey',
      openRouterApiKey: 'openRouterApiKey',
      googleApiKey: 'googleApiKey',
      cerebrasApiKey: 'cerebrasApiKey',
      perplexityApiKey: 'perplexityApiKey',
      openaiApiKey: 'openaiApiKey',
      ollamaBaseUrl: 'ollamaBaseUrl',
    };

    for (const [apiKeyName, settingsKeyName] of Object.entries(keyMappings)) {
      const shellValue = shellKeys[apiKeyName as keyof ApiKeys];
      if (shellValue && !settings[settingsKeyName]) {
        (settings as any)[settingsKeyName] = shellValue;
        hasChanges = true;
      }
    }

    // Handle legacy apiKey field
    if (shellKeys.xaiApiKey && !settings.apiKey && !settings.xaiApiKey) {
      settings.apiKey = shellKeys.xaiApiKey;
      hasChanges = true;
    }

    // Save settings file if there were changes
    if (hasChanges) {
      fs.writeFileSync(settingsFile, JSON.stringify(settings, null, 2));
      // Refresh global shared info after changes
      try {
        const now = new Date();
        fs.utimesSync(settingsFile, now, now);
      } catch (refreshError) {
        // Silently ignore refresh errors
      }
    }
  } catch (error) {
    // Silently ignore errors to avoid disrupting the application
  }
}

export function refreshGlobalSharedInfo(): void {
  try {
    const homeDir = os.homedir();
    const settingsFile = path.join(homeDir, '.giga', 'user-settings.json');

    // If user-settings.json exists, refresh global shared info
    if (fs.existsSync(settingsFile)) {
      // Trigger refresh by updating modification time
      const now = new Date();
      fs.utimesSync(settingsFile, now, now);
    }
  } catch (error) {
    // Silently ignore errors
  }
}

export function loadApiKeys(): ApiKeys {
  try {
    const homeDir = os.homedir();
    const settingsFile = path.join(homeDir, '.giga', 'user-settings.json');

    // Start with environment variables
    const envKeys: ApiKeys = {
      xaiApiKey: process.env.XAI_API_KEY,
      groqApiKey: process.env.GROQ_API_KEY,
      anthropicApiKey: process.env.ANTHROPIC_API_KEY || process.env.CLAUDE_API_KEY,
      openRouterApiKey: process.env.OPENROUTER_API_KEY,
      googleApiKey: process.env.GOOGLE_API_KEY,
      cerebrasApiKey: process.env.CEREBRAS_API_KEY,
      perplexityApiKey: process.env.PERPLEXITY_API_KEY,
      openaiApiKey: process.env.OPENAI_API_KEY,
      ollamaBaseUrl: process.env.OLLAMA_BASE_URL,
    };

    // Check shell files for exported variables
    const shellKeys = checkShellFiles();

    // Save shell keys to settings if they're not already saved
    saveShellKeysToSettings(shellKeys);

    // Load settings file
    let settingsKeys: ApiKeys = {};
    if (fs.existsSync(settingsFile)) {
      const settings: UserSettings = JSON.parse(fs.readFileSync(settingsFile, 'utf8'));
      settingsKeys = {
        xaiApiKey: settings.apiKey || settings.xaiApiKey, // backwards compatibility
        groqApiKey: settings.groqApiKey,
        anthropicApiKey: settings.anthropicApiKey,
        openRouterApiKey: settings.openRouterApiKey,
        googleApiKey: settings.googleApiKey,
        cerebrasApiKey: settings.cerebrasApiKey,
        perplexityApiKey: settings.perplexityApiKey,
        openaiApiKey: settings.openaiApiKey,
        ollamaBaseUrl: settings.ollamaBaseUrl,
      };
    }

    // Priority: env vars > shell files > settings file
    return {
      xaiApiKey: envKeys.xaiApiKey || shellKeys.xaiApiKey || settingsKeys.xaiApiKey,
      groqApiKey: envKeys.groqApiKey || shellKeys.groqApiKey || settingsKeys.groqApiKey,
      anthropicApiKey: envKeys.anthropicApiKey || shellKeys.anthropicApiKey || settingsKeys.anthropicApiKey,
      openRouterApiKey: envKeys.openRouterApiKey || shellKeys.openRouterApiKey || settingsKeys.openRouterApiKey,
      googleApiKey: envKeys.googleApiKey || shellKeys.googleApiKey || settingsKeys.googleApiKey,
      cerebrasApiKey: envKeys.cerebrasApiKey || shellKeys.cerebrasApiKey || settingsKeys.cerebrasApiKey,
      perplexityApiKey: envKeys.perplexityApiKey || shellKeys.perplexityApiKey || settingsKeys.perplexityApiKey,
      openaiApiKey: envKeys.openaiApiKey || shellKeys.openaiApiKey || settingsKeys.openaiApiKey,
      ollamaBaseUrl: envKeys.ollamaBaseUrl || shellKeys.ollamaBaseUrl || settingsKeys.ollamaBaseUrl || 'http://localhost:11434',
    };
  } catch (error) {
    return {};
  }
}

import { exec } from 'child_process';
import { promisify } from 'util';
import { EventEmitter } from 'events';
import { modeManager } from './mode-manager';
import { AgentMode } from '../types';

const execAsync = promisify(exec);

export interface ConfirmationOptions {
  operation: string;
  filename: string;
  showVSCodeOpen?: boolean;
  content?: string;  // Content to show in confirmation dialog
}

export interface ConfirmationResult {
  confirmed: boolean;
  dontAskAgain?: boolean;
  feedback?: string;
}

export class ConfirmationService extends EventEmitter {
  private static instance: ConfirmationService;
  private skipConfirmationThisSession = false;
  private pendingConfirmation: Promise<ConfirmationResult> | null = null;
  private resolveConfirmation: ((result: ConfirmationResult) => void) | null = null;
  private headlessMode = false;

  // Session flags for different operation types
  private sessionFlags = {
    fileOperations: false,
    bashCommands: false,
    allOperations: false
  };

  static getInstance(): ConfirmationService {
    if (!ConfirmationService.instance) {
      ConfirmationService.instance = new ConfirmationService();
    }
    return ConfirmationService.instance;
  }

  constructor() {
    super();
  }

  async requestConfirmation(options: ConfirmationOptions, operationType: 'file' | 'bash' = 'file'): Promise<ConfirmationResult> {
    // In headless mode, auto-approve all operations
    if (this.headlessMode) {
      return { confirmed: true };
    }

    // In GIGA mode, default to no confirmations (full power mode)
    const currentMode = modeManager.getCurrentMode();
    if (currentMode === AgentMode.GIGA) {
      // GIGA mode = full power, no permission requests by default
      return { confirmed: true };
    }

    // In CHILL mode, always ask for confirmation unless user has set session flags
    if (currentMode === AgentMode.CHILL) {
      // Check session flags first
      if (this.sessionFlags.allOperations || 
          (operationType === 'file' && this.sessionFlags.fileOperations) ||
          (operationType === 'bash' && this.sessionFlags.bashCommands)) {
        return { confirmed: true };
      }
      // In CHILL mode, we need to ask for confirmation
    }

    // In PLAN mode, generally approve basic operations but still respect session flags
    if (currentMode === AgentMode.PLAN) {
      if (this.sessionFlags.allOperations || 
          (operationType === 'file' && this.sessionFlags.fileOperations) ||
          (operationType === 'bash' && this.sessionFlags.bashCommands)) {
        return { confirmed: true };
      }
      // For PLAN mode, we might want to be more permissive for read operations
      // but still ask for write operations - the individual tools can decide
    }

    // If VS Code should be opened, try to open it
    if (options.showVSCodeOpen) {
      try {
        await this.openInVSCode(options.filename);
      } catch (error) {
        // If VS Code opening fails, continue without it
        options.showVSCodeOpen = false;
      }
    }

    // Create a promise that will be resolved by the UI component
    this.pendingConfirmation = new Promise<ConfirmationResult>((resolve) => {
      this.resolveConfirmation = resolve;
    });

    // Emit custom event that the UI can listen to (using setImmediate to ensure the UI updates)
    setImmediate(() => {
      this.emit('confirmation-requested', options);
    });

    const result = await this.pendingConfirmation;

    if (result.dontAskAgain) {
      // Set the appropriate session flag based on operation type
      if (operationType === 'file') {
        this.sessionFlags.fileOperations = true;
      } else if (operationType === 'bash') {
        this.sessionFlags.bashCommands = true;
      }
      // Could also set allOperations for global skip
    }

    return result;
  }

  confirmOperation(confirmed: boolean, dontAskAgain?: boolean): void {
    if (this.resolveConfirmation) {
      this.resolveConfirmation({ confirmed, dontAskAgain });
      this.resolveConfirmation = null;
      this.pendingConfirmation = null;
    }
  }

  rejectOperation(feedback?: string): void {
    if (this.resolveConfirmation) {
      this.resolveConfirmation({ confirmed: false, feedback });
      this.resolveConfirmation = null;
      this.pendingConfirmation = null;
    }
  }

  private async openInVSCode(filename: string): Promise<void> {
    // Try different VS Code commands
    const commands = ['code', 'code-insiders', 'codium'];

    for (const cmd of commands) {
      try {
        await execAsync(`which ${cmd}`);
        await execAsync(`${cmd} "${filename}"`);
        return;
      } catch (error) {
        // Continue to next command
        continue;
      }
    }

    throw new Error('VS Code not found');
  }

  isPending(): boolean {
    return this.pendingConfirmation !== null;
  }

  resetSession(): void {
    this.sessionFlags = {
      fileOperations: false,
      bashCommands: false,
      allOperations: false
    };
  }

  getSessionFlags() {
    return { ...this.sessionFlags };
  }

  setHeadlessMode(enabled: boolean): void {
    this.headlessMode = enabled;
  }

  isHeadlessMode(): boolean {
    return this.headlessMode;
  }
}

import path from 'path';
import fs from 'fs/promises';
import os from 'os';
import { ChatEntry } from '../agent/giga-agent';
import { loadApiKeys } from './api-keys';

export interface SavedConversation {
  id: string;
  title: string;
  messages: ChatEntry[];
  createdAt: Date;
  updatedAt: Date;
  model: string;
  messageCount: number;
}

export interface ConversationSummary {
  id: string;
  title: string;
  createdAt: Date;
  updatedAt: Date;
  model: string;
  messageCount: number;
  preview: string; // First user message preview
}

export class ConversationManager {
  private static instance: ConversationManager;
  private conversationsDir: string;

  private constructor() {
    this.conversationsDir = path.join(os.homedir(), '.giga', 'conversations');
  }

  static getInstance(): ConversationManager {
    if (!ConversationManager.instance) {
      ConversationManager.instance = new ConversationManager();
    }
    return ConversationManager.instance;
  }

  private async ensureConversationsDir(): Promise<void> {
    try {
      await fs.mkdir(this.conversationsDir, { recursive: true });
    } catch (error) {
      console.error('Failed to create conversations directory:', error);
    }
  }

  private generateConversationId(): string {
    return `conv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private getConversationPath(id: string): string {
    return path.join(this.conversationsDir, `${id}.json`);
  }

  async generateTitle(messages: ChatEntry[]): Promise<string> {
    const apiKeys = loadApiKeys();
    const groqApiKey = apiKeys.groqApiKey || process.env.GROQ_API_KEY;

    if (!groqApiKey) {
      // Fallback to first user message if no API key
      const firstUserMessage = messages.find(m => m.type === 'user');
      return firstUserMessage?.content.slice(0, 50) + '...' || 'Untitled Conversation';
    }

    try {
      // Get first few meaningful messages for context
      const contextMessages = messages
        .filter(m => m.type === 'user' || m.type === 'assistant')
        .slice(0, 6)
        .map(m => `${m.type}: ${m.content}`)
        .join('\n');

      const response = await fetch('https://api.groq.com/openai/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${groqApiKey}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          model: 'llama-3.3-70b-versatile',
          messages: [
            {
              role: 'system',
              content: 'Generate a concise 3-6 word title for this coding conversation. Focus on the main task/topic. Examples: "React Component Bug Fix", "Database Schema Design", "API Integration Setup".'
            },
            {
              role: 'user',
              content: `Conversation:\n${contextMessages}\n\nTitle:`
            }
          ],
          max_tokens: 20,
          temperature: 0.1
        })
      });

      if (response.ok) {
        const data = await response.json() as any;
        const title = data.choices?.[0]?.message?.content?.trim();
        if (title && title.length > 0) {
          return title.replace(/['"]/g, ''); // Remove quotes if any
        }
      }
    } catch (error) {
      console.error('Failed to generate title:', error);
    }

    // Fallback to first user message
    const firstUserMessage = messages.find(m => m.type === 'user');
    return firstUserMessage?.content.slice(0, 50) + '...' || 'Untitled Conversation';
  }

  async saveConversation(messages: ChatEntry[], model: string, existingId?: string): Promise<string> {
    await this.ensureConversationsDir();

    const id = existingId || this.generateConversationId();
    const now = new Date();

    // Generate title if this is a new conversation or if it doesn't have many messages yet
    let title = 'New Conversation';
    if (messages.length >= 2) {
      title = await this.generateTitle(messages);
    }

    const conversation: SavedConversation = {
      id,
      title,
      messages,
      createdAt: existingId ? (await this.loadConversation(id))?.createdAt || now : now,
      updatedAt: now,
      model,
      messageCount: messages.filter(m => m.type === 'user' || m.type === 'assistant').length
    };

    const filePath = this.getConversationPath(id);
    await fs.writeFile(filePath, JSON.stringify(conversation, null, 2));
    return id;
  }

  async loadConversation(id: string): Promise<SavedConversation | null> {
    try {
      const filePath = this.getConversationPath(id);
      const data = await fs.readFile(filePath, 'utf-8');
      const conversation = JSON.parse(data);

      // Convert date strings back to Date objects
      conversation.createdAt = new Date(conversation.createdAt);
      conversation.updatedAt = new Date(conversation.updatedAt);
      conversation.messages = conversation.messages.map((msg: any) => ({
        ...msg,
        timestamp: new Date(msg.timestamp)
      }));

      return conversation;
    } catch (error) {
      console.error(`Failed to load conversation ${id}:`, error);
      return null;
    }
  }

  async listConversations(): Promise<ConversationSummary[]> {
    await this.ensureConversationsDir();

    try {
      const files = await fs.readdir(this.conversationsDir);
      const jsonFiles = files.filter(f => f.endsWith('.json'));

      const summaries: ConversationSummary[] = [];

      for (const file of jsonFiles) {
        try {
          const id = file.replace('.json', '');
          const conversation = await this.loadConversation(id);

          if (conversation) {
            const firstUserMessage = conversation.messages.find(m => m.type === 'user');
            const preview = firstUserMessage?.content.slice(0, 100) + '...' || 'No messages';

            summaries.push({
              id: conversation.id,
              title: conversation.title,
              createdAt: conversation.createdAt,
              updatedAt: conversation.updatedAt,
              model: conversation.model,
              messageCount: conversation.messageCount,
              preview
            });
          }
        } catch (error) {
          console.error(`Failed to process conversation file ${file}:`, error);
        }
      }

      // Sort by most recently updated first
      return summaries.sort((a, b) => b.updatedAt.getTime() - a.updatedAt.getTime());
    } catch (error) {
      console.error('Failed to list conversations:', error);
      return [];
    }
  }

  async deleteConversation(id: string): Promise<boolean> {
    try {
      const filePath = this.getConversationPath(id);
      await fs.unlink(filePath);
      return true;
    } catch (error) {
      console.error(`Failed to delete conversation ${id}:`, error);
      return false;
    }
  }

  async searchConversations(query: string): Promise<ConversationSummary[]> {
    const allConversations = await this.listConversations();

    if (!query.trim()) {
      return allConversations;
    }

    const lowerQuery = query.toLowerCase();

    return allConversations.filter(conv => 
      conv.title.toLowerCase().includes(lowerQuery) ||
      conv.preview.toLowerCase().includes(lowerQuery)
    );
  }
}

import * as fs from 'fs';
import * as path from 'path';
import { getPromptsAsSystemPrompt } from './prompts';

export function loadCustomInstructions(workingDirectory: string = process.cwd()): string | null {
  try {
    const instructionsPath = path.join(workingDirectory, '.giga', 'GIGA.md');

    let customInstructions = '';

    // Load global custom prompts first
    const globalPrompts = getPromptsAsSystemPrompt();
    if (globalPrompts) {
      customInstructions += globalPrompts;
    }

    // Then load project-specific GIGA.md if it exists
    if (fs.existsSync(instructionsPath)) {
      const projectInstructions = fs.readFileSync(instructionsPath, 'utf-8');
      if (projectInstructions.trim()) {
        if (customInstructions) {
          customInstructions += '\n\n' + projectInstructions.trim();
        } else {
          customInstructions = projectInstructions.trim();
        }
      }
    }

    return customInstructions || null;
  } catch (error) {
    console.warn('Failed to load custom instructions:', error);
    return null;
  }
}

import { PROVIDER_MODELS, ProviderName } from './provider-models';
import { fetchOllamaModels } from './ollama-models';

export interface ModelInfo {
  id: string;
  name?: string;
  description?: string;
  provider?: string;
}

export interface ModelFetchResult {
  success: boolean;
  models: string[];
  error?: string;
}

// Fetch models from OpenRouter API
async function fetchOpenRouterModels(apiKey: string): Promise<ModelFetchResult> {
  try {
    const response = await fetch('https://openrouter.ai/api/v1/models', {
      headers: {
        'Authorization': `Bearer ${apiKey}`,
      },
      signal: AbortSignal.timeout(10000),
    });

    if (!response.ok) {
      return { success: false, models: [], error: `HTTP ${response.status}` };
    }

    const data = await response.json() as any;
    const models = data.data || data;
    const modelIds = Array.isArray(models) 
      ? models.map((model: any) => model.id).filter(Boolean)
      : [];

    return { success: true, models: modelIds };
  } catch (error: any) {
    return { success: false, models: [], error: error.message };
  }
}

// Fetch models from Google AI API
async function fetchGoogleModels(apiKey: string): Promise<ModelFetchResult> {
  try {
    const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models?key=${apiKey}`, {
      signal: AbortSignal.timeout(10000),
    });

    if (!response.ok) {
      return { success: false, models: [], error: `HTTP ${response.status}` };
    }

    const data = await response.json() as any;
    const models = data.models || [];
    const modelIds = models
      .map((model: any) => model.name?.replace('models/', '') || model.id)
      .filter(Boolean);

    return { success: true, models: modelIds };
  } catch (error: any) {
    return { success: false, models: [], error: error.message };
  }
}

