import * as fs from 'fs';
import * as path from 'path';

export interface FileInfo {
  name: string;
  relativePath: string;
  isDirectory: boolean;
}

/**
 * Get all files in the current directory and subdirectories
 */
export function getAllFiles(rootDir: string = process.cwd(), maxDepth: number = 3): FileInfo[] {
  const files: FileInfo[] = [];

  function walkDirectory(dir: string, currentDepth: number = 0) {
    if (currentDepth > maxDepth) return;

    try {
      const entries = fs.readdirSync(dir, { withFileTypes: true });

      for (const entry of entries) {
        // Skip hidden files and common ignore patterns
        if (entry.name.startsWith('.') || 
            entry.name === 'node_modules' || 
            entry.name === 'dist' || 
            entry.name === 'build' ||
            entry.name === '.git') {
          continue;
        }

        const fullPath = path.join(dir, entry.name);
        const relativePath = path.relative(rootDir, fullPath);

        if (entry.isDirectory()) {
          files.push({
            name: entry.name,
            relativePath,
            isDirectory: true
          });
          walkDirectory(fullPath, currentDepth + 1);
        } else {
          files.push({
            name: entry.name,
            relativePath,
            isDirectory: false
          });
        }
      }
    } catch (error) {
      // Ignore permission errors and continue
    }
  }

  walkDirectory(rootDir);
  return files;
}

/**
 * Extract file search query from input text after @ symbol
 * Only returns a result if @ is the last "word" (no spaces after @)
 */
export function extractFileQuery(input: string): { beforeAt: string; query: string; afterAt: string; isDirectory: boolean } | null {
  const atIndex = input.lastIndexOf('@');
  if (atIndex === -1) return null;

  // Check if there are any spaces after the @ symbol
  const afterAt = input.substring(atIndex + 1);
  if (afterAt.includes(' ')) {
    // There's a space after @, so this is not an active file query
    return null;
  }

  const beforeAt = input.substring(0, atIndex);
  const query = afterAt; // Everything after @ until end of string
  const isDirectory = query.endsWith('/');

  // Only show file finder if there's at least the @ symbol
  // Allow empty query to show all files/directories
  return { beforeAt, query, afterAt: '', isDirectory };
}

/**
 * Filter files to get only files or only directories based on query
 */
export function getFilteredItems(files: FileInfo[], query: string, isDirectory: boolean): string[] {
  // Remove trailing slash for directory search
  const searchQuery = isDirectory ? query.slice(0, -1) : query;

  if (isDirectory) {
    // Filter for directories only
    let results = files
      .filter(file => file.isDirectory)
      .map(file => file.relativePath + '/');

    // If there's a search query, filter by it
    if (searchQuery) {
      results = results.filter(path => 
        path.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    return results.sort().slice(0, 10); // Limit results
  } else {
    // Filter for files only
    let results = files
      .filter(file => !file.isDirectory)
      .map(file => file.relativePath);

    // If there's a search query, filter by it
    if (query) {
      results = results.filter(path => 
        path.toLowerCase().includes(query.toLowerCase())
      );
    }

    return results.sort().slice(0, 10); // Limit results
  }
}

/**
 * Replace the file query in input with the selected file path
 */
export function replaceFileQuery(input: string, selectedFile: string): string {
  const queryInfo = extractFileQuery(input);
  if (!queryInfo) return input;

  return queryInfo.beforeAt + selectedFile + queryInfo.afterAt;
}

interface FuzzyMatch {
  item: any;
  score: number;
  matches: number[];
}

/**
 * Simple fuzzy matching algorithm that scores matches based on:
 * - Exact matches get highest score
 * - Sequential character matches get high score
 * - Character matches get base score
 * - Shorter strings with matches get bonus points
 */
export function fuzzyMatch(query: string, target: string): FuzzyMatch | null {
  if (!query || !target) return null;

  const lowerQuery = query.toLowerCase();
  const lowerTarget = target.toLowerCase();

  // Exact match gets maximum score
  if (lowerTarget === lowerQuery) {
    return {
      item: target,
      score: 1000,
      matches: Array.from({ length: target.length }, (_, i) => i)
    };
  }

  // If target starts with query, give it high score
  if (lowerTarget.startsWith(lowerQuery)) {
    return {
      item: target,
      score: 900,
      matches: Array.from({ length: lowerQuery.length }, (_, i) => i)
    };
  }

  const matches: number[] = [];
  let queryIndex = 0;
  let score = 0;
  let consecutiveMatches = 0;

  for (let i = 0; i < lowerTarget.length && queryIndex < lowerQuery.length; i++) {
    if (lowerTarget[i] === lowerQuery[queryIndex]) {
      matches.push(i);
      queryIndex++;
      consecutiveMatches++;

      // Base score for each match
      score += 10;

      // Bonus for consecutive matches
      if (consecutiveMatches > 1) {
        score += consecutiveMatches * 5;
      }

      // Bonus for matches at the beginning
      if (i === queryIndex - 1) {
        score += 20;
      }
    } else {
      consecutiveMatches = 0;
    }
  }

  // If we didn't match all query characters, no match
  if (queryIndex < lowerQuery.length) {
    return null;
  }

  // Bonus for shorter targets (more relevant)
  const lengthBonus = Math.max(0, 100 - target.length);
  score += lengthBonus;

  return {
    item: target,
    score,
    matches
  };
}

export function fuzzySearch<T>(
  query: string,
  items: T[],
  getText: (item: T) => string,
  limit = 10
): T[] {
  if (!query.trim()) {
    return items.slice(0, limit);
  }

  const matches: Array<{ item: T; score: number }> = [];

  for (const item of items) {
    const text = getText(item);
    const match = fuzzyMatch(query, text);

    if (match) {
      matches.push({ item, score: match.score });
    }
  }

  // Sort by score (descending) and return items
  return matches
    .sort((a, b) => b.score - a.score)
    .slice(0, limit)
    .map(match => match.item);
}

import * as fs from "fs";
import * as path from "path";
import { sessionManager } from "./session-manager";
import { AddedModel, loadAddedModels, saveAddedModels } from "./added-models";

export interface InstanceModelPreferences {
  instanceId: string;
  favoriteModels: string[];
  recentlyUsedModels: string[];
  lastUpdated: string;
}

const getInstancePreferencesPath = (): string => {
  const sessionInfo = sessionManager.getSessionInfo();
  if (!sessionInfo) {
    throw new Error('No active session found');
  }

  const homeDir = require('os').homedir();
  const sessionDir = path.join(homeDir, '.giga', 'sessions', sessionInfo.instanceId);

  // Ensure session directory exists
  if (!fs.existsSync(sessionDir)) {
    fs.mkdirSync(sessionDir, { recursive: true, mode: 0o700 });
  }

  return path.join(sessionDir, 'model-preferences.json');
};

export function loadInstanceModelPreferences(): InstanceModelPreferences {
  try {
    const preferencesPath = getInstancePreferencesPath();
    const sessionInfo = sessionManager.getSessionInfo();

    if (!sessionInfo) {
      throw new Error('No active session found');
    }

    if (!fs.existsSync(preferencesPath)) {
      // Return default preferences for new session
      return {
        instanceId: sessionInfo.instanceId,
        favoriteModels: [],
        recentlyUsedModels: sessionInfo.currentModel ? [sessionInfo.currentModel] : [],
        lastUpdated: new Date().toISOString()
      };
    }

    const data: InstanceModelPreferences = JSON.parse(fs.readFileSync(preferencesPath, 'utf8'));
    return data;
  } catch (error) {
    console.error('Error loading instance model preferences:', error);
    const sessionInfo = sessionManager.getSessionInfo();
    return {
      instanceId: sessionInfo?.instanceId || 'unknown',
      favoriteModels: [],
      recentlyUsedModels: [],
      lastUpdated: new Date().toISOString()
    };
  }
}

export function saveInstanceModelPreferences(preferences: InstanceModelPreferences): void {
  try {
    const preferencesPath = getInstancePreferencesPath();
    preferences.lastUpdated = new Date().toISOString();

    fs.writeFileSync(
      preferencesPath, 
      JSON.stringify(preferences, null, 2), 
      { mode: 0o600 }
    );
  } catch (error) {
    console.error('Error saving instance model preferences:', error);
  }
}

export function addModelToRecentlyUsed(modelName: string): void {
  try {
    const preferences = loadInstanceModelPreferences();

    // Remove model if it already exists in the list
    preferences.recentlyUsedModels = preferences.recentlyUsedModels.filter(m => m !== modelName);

    // Add to the beginning of the list
    preferences.recentlyUsedModels.unshift(modelName);

    // Keep only the last 10 recently used models
    preferences.recentlyUsedModels = preferences.recentlyUsedModels.slice(0, 10);

    saveInstanceModelPreferences(preferences);
  } catch (error) {
    console.error('Error adding model to recently used:', error);
  }
}

export function toggleModelFavorite(modelName: string): boolean {
  try {
    const preferences = loadInstanceModelPreferences();

    const isFavorite = preferences.favoriteModels.includes(modelName);

    if (isFavorite) {
      // Remove from favorites
      preferences.favoriteModels = preferences.favoriteModels.filter(m => m !== modelName);
    } else {
      // Add to favorites
      preferences.favoriteModels.push(modelName);
    }

    saveInstanceModelPreferences(preferences);
    return !isFavorite; // Return new favorite status
  } catch (error) {
    console.error('Error toggling model favorite:', error);
    return false;
  }
}

export function getInstanceAvailableModels(): { model: string; description: string; isFavorite: boolean; isRecentlyUsed: boolean }[] {
  try {
    const preferences = loadInstanceModelPreferences();
    const globalModels = loadAddedModels();

    // Only show global added models (no built-in models)
    const modelsWithPreferences = globalModels.map(m => ({
      model: m.modelName,
      description: `${m.modelName} (${m.providerName})`,
      isFavorite: preferences.favoriteModels.includes(m.modelName),
      isRecentlyUsed: preferences.recentlyUsedModels.includes(m.modelName)
    }));

    // Sort: favorites first, then recently used, then alphabetically
    return modelsWithPreferences.sort((a, b) => {
      if (a.isFavorite && !b.isFavorite) return -1;
      if (!a.isFavorite && b.isFavorite) return 1;
      if (a.isRecentlyUsed && !b.isRecentlyUsed) return -1;
      if (!a.isRecentlyUsed && b.isRecentlyUsed) return 1;
      return a.model.localeCompare(b.model);
    });
  } catch (error) {
    console.error('Error getting instance available models:', error);
    return [];
  }
}

// Update recently used when model is selected
export function onModelSelected(modelName: string): void {
  addModelToRecentlyUsed(modelName);
  sessionManager.setCurrentModel(modelName);
}

import { AgentMode, ModeConfig } from '../types';

export class ModeManager {
  private static instance: ModeManager;
  private currentMode: AgentMode = AgentMode.CHILL;
  private modeConfigs: Record<AgentMode, ModeConfig> = {
    [AgentMode.PLAN]: {
      mode: AgentMode.PLAN,
      allowExpertModels: false,
      requireConfirmation: false,
      displayName: 'üìã PLAN MODE',
      description: 'Planning and thinking only - no expert models'
    },
    [AgentMode.CHILL]: {
      mode: AgentMode.CHILL,
      allowExpertModels: true,
      requireConfirmation: true,
      displayName: 'üòå CHILL MODE',
      description: 'All models available - asks permission for tool calls'
    },
    [AgentMode.GIGA]: {
      mode: AgentMode.GIGA,
      allowExpertModels: true,
      requireConfirmation: false,
      displayName: '‚ö° GIGA MODE',
      description: 'Full power - no permission requests'
    }
  };

  private constructor() {}

  static getInstance(): ModeManager {
    if (!ModeManager.instance) {
      ModeManager.instance = new ModeManager();
    }
    return ModeManager.instance;
  }

  getCurrentMode(): AgentMode {
    return this.currentMode;
  }

  getCurrentModeConfig(): ModeConfig {
    return this.modeConfigs[this.currentMode];
  }

  getAllModes(): AgentMode[] {
    return [AgentMode.PLAN, AgentMode.CHILL, AgentMode.GIGA];
  }

  cycleMode(): AgentMode {
    const modes = this.getAllModes();
    const currentIndex = modes.indexOf(this.currentMode);
    const nextIndex = (currentIndex + 1) % modes.length;
    this.currentMode = modes[nextIndex];
    return this.currentMode;
  }

  setMode(mode: AgentMode): void {
    this.currentMode = mode;
  }

  shouldAllowExpertModels(): boolean {
    return this.modeConfigs[this.currentMode].allowExpertModels;
  }

  shouldRequireConfirmation(): boolean {
    return this.modeConfigs[this.currentMode].requireConfirmation;
  }

  getModeDisplayName(): string {
    return this.modeConfigs[this.currentMode].displayName;
  }

  getModeDescription(): string {
    return this.modeConfigs[this.currentMode].description;
  }
}

export const modeManager = ModeManager.getInstance();

