// Check if a model ID is in OpenRouter format (author/model)
export function isOpenRouterModel(modelId: string): boolean {
  return modelId.includes('/') && modelId.split('/').length === 2;
}

// Get providers for a model with fallback
export async function getModelProvidersWithFallback(
  modelId: string, 
  apiKey: string
): Promise<OpenRouterProvider[]> {
  // Only try to fetch providers for OpenRouter models
  if (!isOpenRouterModel(modelId)) {
    console.warn(`Model ${modelId} is not in OpenRouter format (author/model), skipping provider fetch`);
    return [];
  }

  const result = await fetchModelProviders(modelId, apiKey);

  if (result.success && result.providers.length > 0) {
    return result.providers;
  }

  // If API call failed, return empty array instead of hardcoded fallbacks
  // This forces the user to check their API key or model availability
  console.warn(`Failed to fetch providers for model ${modelId}: ${result.error}`);
  return [];
}

import * as fs from "fs";
import * as path from "path";
import * as os from "os";

export interface CustomPrompt {
  name: string;
  content: string;
  dateAdded: string;
}

interface PromptsStorage {
  prompts: CustomPrompt[];
}

const getStorageFile = (): string => {
  const homeDir = os.homedir();
  const gigaDir = path.join(homeDir, '.giga');

  // Create .giga directory if it doesn't exist
  if (!fs.existsSync(gigaDir)) {
    fs.mkdirSync(gigaDir, { mode: 0o700 });
  }

  return path.join(gigaDir, 'prompts.json');
};

export function loadPrompts(): CustomPrompt[] {
  try {
    const storageFile = getStorageFile();

    if (!fs.existsSync(storageFile)) {
      return [];
    }

    const data: PromptsStorage = JSON.parse(fs.readFileSync(storageFile, 'utf8'));
    return data.prompts || [];
  } catch (error) {
    console.error('Error loading prompts:', error);
    return [];
  }
}

export function savePrompts(prompts: CustomPrompt[]): void {
  try {
    const storageFile = getStorageFile();
    const data: PromptsStorage = { prompts };

    fs.writeFileSync(storageFile, JSON.stringify(data, null, 2), { mode: 0o600 });
  } catch (error) {
    console.error('Error saving prompts:', error);
  }
}

export function addPrompt(name: string, content: string): void {
  const prompts = loadPrompts();

  // Check if prompt already exists
  const exists = prompts.some(p => p.name === name);
  if (!exists) {
    const newPrompt: CustomPrompt = {
      name,
      content,
      dateAdded: new Date().toISOString(),
    };

    prompts.push(newPrompt);
    savePrompts(prompts);
  }
}

export function deletePrompt(name: string): boolean {
  const prompts = loadPrompts();
  const initialLength = prompts.length;

  const filteredPrompts = prompts.filter(p => p.name !== name);

  if (filteredPrompts.length < initialLength) {
    savePrompts(filteredPrompts);
    return true;
  }

  return false;
}

export function getAllPrompts(): CustomPrompt[] {
  return loadPrompts();
}

export function getPromptByName(name: string): CustomPrompt | undefined {
  const prompts = loadPrompts();
  return prompts.find(p => p.name === name);
}

export function isPromptExists(name: string): boolean {
  const prompts = loadPrompts();
  return prompts.some(p => p.name === name);
}

export function getPromptsAsSystemPrompt(): string {
  const prompts = loadPrompts();
  if (prompts.length === 0) {
    return '';
  }

  return prompts.map(prompt => prompt.content).join('\n\n');
}

import * as fs from "fs";
import * as path from "path";
import { sessionManager } from "./session-manager";

export interface ModelProviderPreference {
  modelId: string;
  preferredProvider: string;
  providerName: string;
  dateAdded: string;
  lastUsed: string;
}

export interface ProviderPreferences {
  instanceId: string;
  modelProviderMap: ModelProviderPreference[];
  lastUpdated: string;
}

const getProviderPreferencesPath = (): string => {
  const sessionInfo = sessionManager.getSessionInfo();
  if (!sessionInfo) {
    throw new Error('No active session found');
  }

  const homeDir = require('os').homedir();
  const sessionDir = path.join(homeDir, '.giga', 'sessions', sessionInfo.instanceId);

  // Ensure session directory exists
  if (!fs.existsSync(sessionDir)) {
    fs.mkdirSync(sessionDir, { recursive: true, mode: 0o700 });
  }

  return path.join(sessionDir, 'provider-preferences.json');
};

export function loadProviderPreferences(): ProviderPreferences {
  try {
    const preferencesPath = getProviderPreferencesPath();
    const sessionInfo = sessionManager.getSessionInfo();

    if (!sessionInfo) {
      throw new Error('No active session found');
    }

    if (!fs.existsSync(preferencesPath)) {
      // Return default preferences for new session
      return {
        instanceId: sessionInfo.instanceId,
        modelProviderMap: [],
        lastUpdated: new Date().toISOString()
      };
    }

    const data: ProviderPreferences = JSON.parse(fs.readFileSync(preferencesPath, 'utf8'));
    return data;
  } catch (error) {
    console.error('Error loading provider preferences:', error);
    const sessionInfo = sessionManager.getSessionInfo();
    return {
      instanceId: sessionInfo?.instanceId || 'unknown',
      modelProviderMap: [],
      lastUpdated: new Date().toISOString()
    };
  }
}

export function saveProviderPreferences(preferences: ProviderPreferences): void {
  try {
    const preferencesPath = getProviderPreferencesPath();
    preferences.lastUpdated = new Date().toISOString();

    fs.writeFileSync(
      preferencesPath, 
      JSON.stringify(preferences, null, 2), 
      { mode: 0o600 }
    );
  } catch (error) {
    console.error('Error saving provider preferences:', error);
  }
}

export function setModelProviderPreference(
  modelId: string, 
  providerId: string, 
  providerName: string
): void {
  try {
    const preferences = loadProviderPreferences();
    const now = new Date().toISOString();

    // Remove existing preference for this model
    preferences.modelProviderMap = preferences.modelProviderMap.filter(
      p => p.modelId !== modelId
    );

    // Add new preference
    preferences.modelProviderMap.push({
      modelId,
      preferredProvider: providerId,
      providerName,
      dateAdded: now,
      lastUsed: now
    });

    saveProviderPreferences(preferences);
  } catch (error) {
    console.error('Error setting model provider preference:', error);
  }
}

export function getModelProviderPreference(modelId: string): ModelProviderPreference | null {
  try {
    const preferences = loadProviderPreferences();
    return preferences.modelProviderMap.find(p => p.modelId === modelId) || null;
  } catch (error) {
    console.error('Error getting model provider preference:', error);
    return null;
  }
}

export function updateModelProviderLastUsed(modelId: string): void {
  try {
    const preferences = loadProviderPreferences();
    const preference = preferences.modelProviderMap.find(p => p.modelId === modelId);

    if (preference) {
      preference.lastUsed = new Date().toISOString();
      saveProviderPreferences(preferences);
    }
  } catch (error) {
    console.error('Error updating model provider last used:', error);
  }
}

export function removeModelProviderPreference(modelId: string): void {
  try {
    const preferences = loadProviderPreferences();
    preferences.modelProviderMap = preferences.modelProviderMap.filter(
      p => p.modelId !== modelId
    );
    saveProviderPreferences(preferences);
  } catch (error) {
    console.error('Error removing model provider preference:', error);
  }
}

export function getAllModelProviderPreferences(): ModelProviderPreference[] {
  try {
    const preferences = loadProviderPreferences();
    return preferences.modelProviderMap.sort((a, b) => 
      new Date(b.lastUsed).getTime() - new Date(a.lastUsed).getTime()
    );
  } catch (error) {
    console.error('Error getting all model provider preferences:', error);
    return [];
  }
}

import * as fs from "fs";
import * as path from "path";
import * as os from "os";
import { randomUUID } from "crypto";

export interface SessionConfig {
  instanceId: string;
  currentModel: string;
  temperature?: number;
  createdAt: string;
  lastUsed: string;
}

class SessionManager {
  private instanceId: string;
  private sessionDir: string;

  constructor() {
    this.instanceId = randomUUID();
    this.sessionDir = this.getSessionDir();
    this.ensureSessionDir();
    this.saveSessionConfig();
  }

  private getGigaDir(): string {
    const homeDir = os.homedir();
    const gigaDir = path.join(homeDir, '.giga');

    if (!fs.existsSync(gigaDir)) {
      fs.mkdirSync(gigaDir, { mode: 0o700 });
    }

    return gigaDir;
  }

  private getSessionDir(): string {
    const gigaDir = this.getGigaDir();
    return path.join(gigaDir, 'sessions', this.instanceId);
  }

  private ensureSessionDir(): void {
    if (!fs.existsSync(this.sessionDir)) {
      fs.mkdirSync(this.sessionDir, { recursive: true, mode: 0o700 });
    }
  }

  private getSessionConfigPath(): string {
    return path.join(this.sessionDir, 'config.json');
  }

  private saveSessionConfig(): void {
    try {
      const config: SessionConfig = {
        instanceId: this.instanceId,
        currentModel: 'moonshotai/kimi-k2-instruct', // default model
        createdAt: new Date().toISOString(),
        lastUsed: new Date().toISOString()
      };

      fs.writeFileSync(
        this.getSessionConfigPath(), 
        JSON.stringify(config, null, 2), 
        { mode: 0o600 }
      );
    } catch (error) {
      console.error('Error saving session config:', error);
    }
  }

  public getInstanceId(): string {
    return this.instanceId;
  }

  public getCurrentModel(): string {
    try {
      const configPath = this.getSessionConfigPath();

      if (!fs.existsSync(configPath)) {
        return 'moonshotai/kimi-k2-instruct'; // default fallback
      }

      const config: SessionConfig = JSON.parse(fs.readFileSync(configPath, 'utf8'));
      return config.currentModel || 'moonshotai/kimi-k2-instruct';
    } catch (error) {
      console.error('Error loading current model:', error);
      return 'moonshotai/kimi-k2-instruct';
    }
  }

  public setCurrentModel(modelName: string): void {
    try {
      const configPath = this.getSessionConfigPath();
      let config: SessionConfig;

      if (fs.existsSync(configPath)) {
        config = JSON.parse(fs.readFileSync(configPath, 'utf8'));
      } else {
        config = {
          instanceId: this.instanceId,
          currentModel: modelName,
          createdAt: new Date().toISOString(),
          lastUsed: new Date().toISOString()
        };
      }

      config.currentModel = modelName;
      config.lastUsed = new Date().toISOString();

      fs.writeFileSync(
        configPath, 
        JSON.stringify(config, null, 2), 
        { mode: 0o600 }
      );
    } catch (error) {
      console.error('Error saving current model:', error);
    }
  }

  public getTemperature(): number {
    try {
      const configPath = this.getSessionConfigPath();

      if (!fs.existsSync(configPath)) {
        return 0.7; // default temperature
      }

      const config: SessionConfig = JSON.parse(fs.readFileSync(configPath, 'utf8'));
      return config.temperature ?? 0.7;
    } catch (error) {
      console.error('Error loading temperature:', error);
      return 0.7;
    }
  }

  public setTemperature(temperature: number): void {
    try {
      const configPath = this.getSessionConfigPath();
      let config: SessionConfig;

      if (fs.existsSync(configPath)) {
        config = JSON.parse(fs.readFileSync(configPath, 'utf8'));
      } else {
        config = {
          instanceId: this.instanceId,
          currentModel: 'moonshotai/kimi-k2-instruct',
          temperature: temperature,
          createdAt: new Date().toISOString(),
          lastUsed: new Date().toISOString()
        };
      }

      config.temperature = temperature;
      config.lastUsed = new Date().toISOString();

      fs.writeFileSync(
        configPath, 
        JSON.stringify(config, null, 2), 
        { mode: 0o600 }
      );
    } catch (error) {
      console.error('Error saving temperature:', error);
    }
  }

  public getSessionInfo(): SessionConfig | null {
    try {
      const configPath = this.getSessionConfigPath();

      if (!fs.existsSync(configPath)) {
        return null;
      }

      return JSON.parse(fs.readFileSync(configPath, 'utf8'));
    } catch (error) {
      console.error('Error loading session info:', error);
      return null;
    }
  }

  // Cleanup old sessions (optional - called periodically)
  public static cleanupOldSessions(olderThanDays: number = 7): void {
    try {
      const homeDir = os.homedir();
      const sessionsDir = path.join(homeDir, '.giga', 'sessions');

      if (!fs.existsSync(sessionsDir)) {
        return;
      }

      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - olderThanDays);

      const sessionDirs = fs.readdirSync(sessionsDir);

      for (const sessionDir of sessionDirs) {
        const sessionPath = path.join(sessionsDir, sessionDir);
        const configPath = path.join(sessionPath, 'config.json');

        if (fs.existsSync(configPath)) {
          try {
            const config: SessionConfig = JSON.parse(fs.readFileSync(configPath, 'utf8'));
            const lastUsed = new Date(config.lastUsed);

            if (lastUsed < cutoffDate) {
              fs.rmSync(sessionPath, { recursive: true, force: true });
            }
          } catch (error) {
            // If we can't read the config, consider it for cleanup
            const stats = fs.statSync(sessionPath);
            if (stats.mtime < cutoffDate) {
              fs.rmSync(sessionPath, { recursive: true, force: true });
            }
          }
        }
      }
    } catch (error) {
      console.error('Error cleaning up old sessions:', error);
    }
  }
}

// Export singleton instance
export const sessionManager = new SessionManager();

import { get_encoding, encoding_for_model, Tiktoken } from 'tiktoken';

export class TokenCounter {
  private encoder: Tiktoken;

  constructor(model: string = 'gpt-4') {
    try {
      // Try to get encoding for specific model
      this.encoder = encoding_for_model(model as any);
    } catch {
      // Fallback to cl100k_base (used by GPT-4 and most modern models)
      this.encoder = get_encoding('cl100k_base');
    }
  }

  /**
   * Count tokens in a string
   */
  countTokens(text: string): number {
    if (!text) return 0;
    return this.encoder.encode(text).length;
  }

  /**
   * Count tokens in messages array (for chat completions)
   */
  countMessageTokens(messages: Array<{ role: string; content: string | null; [key: string]: any }>): number {
    let totalTokens = 0;

    for (const message of messages) {
      // Every message follows <|start|>{role/name}\n{content}<|end|\>\n
      totalTokens += 3; // Base tokens per message

      if (message.content && typeof message.content === 'string') {
        totalTokens += this.countTokens(message.content);
      }

      if (message.role) {
        totalTokens += this.countTokens(message.role);
      }

      // Add extra tokens for tool calls if present
      if (message.tool_calls) {
        totalTokens += this.countTokens(JSON.stringify(message.tool_calls));
      }
    }

    totalTokens += 3; // Every reply is primed with <|start|>assistant<|message|>

    return totalTokens;
  }

  /**
   * Estimate tokens for streaming content
   * This is an approximation since we don't have the full response yet
   */
  estimateStreamingTokens(accumulatedContent: string): number {
    return this.countTokens(accumulatedContent);
  }

  /**
   * Clean up resources
   */
  dispose(): void {
    this.encoder.free();
  }
}

/**
 * Create a token counter instance
 */
export function createTokenCounter(model?: string): TokenCounter {
  return new TokenCounter(model);
}
